import React, { useEffect, useState, useContext } from 'react';

import AuthContext from '../../context/AuthContext';

import SocialDrawer from '../../components/Navigation/SocialDrawer';
import Skyliner from '../../components/Social/Profile/Skyliner';
import ProfileNav from '../../components/Social/Profile/ProfileNav';
import TimelineTab from '../../components/Social/Profile/TimelineTab';
import AboutTab from '../../components/Social/Profile/About';
import FriendsTab from '../../components/Social/Profile/Friends';
import PhotosTab from '../../components/Social/Profile/Photos';


function Profile(props) {

    const [user, setUser] = React.useState([]);
    const [onLoad, setOnLoad] = React.useState(false);
    const [tabFunc, setTabFunc] = React.useState({onChange: handleTabChange});
    const [profileTimeline, setProfileTimeline] = React.useState(true);
    const [About, setAbout] = React.useState(false);
    const [Friends, setFriends] = React.useState(false);
    const [Photos, setPhotos] = React.useState(false);

    const auth = useContext(AuthContext);

    function handleTabChange(tab) {
        console.log(tab)
        switch (tab) {
            case 'Timeline':
                setProfileTimeline(true);
                setAbout(false);
                setFriends(false);
                setPhotos(false);
                break;
            case 'About':
                setAbout(true);
                setProfileTimeline(false);
                setFriends(false);
                setPhotos(false);
                break;
            case 'Friends':
                setFriends(true);
                setProfileTimeline(false);
                setAbout(false);
                setPhotos(false);
                break;
            case 'Photos': 
                setPhotos(true);
                setProfileTimeline(false);
                setAbout(false);
                setFriends(false);
                break;
            default:
                break; 
        }
    };

    function renderTabs() {
        if(profileTimeline){return <TimelineTab info={user.info} />}
        if(About){return <AboutTab handle={props.match.params} auth={auth.user} info={user.info} />}
        if(Friends){return <FriendsTab info={user.info} />}
        if(Photos){return <PhotosTab info={user.info} />}
    }

    useEffect(() => {
        const { handle } = props.match.params
        setOnLoad(true);
        if(!onLoad){
            fetch('/api/social/getProfile', {
                method: 'POST',
                body: JSON.stringify({username: handle}),
                headers: {'Content-Type': 'application/json'}
            }).then(res => res.json())
            .then((result) => {
                console.log(result);
                setUser({...user, ...result})
            });
        }
    })

    return (
        <div className="container-fluid colp">
            <div className="row marg">
                <div className="col-12 colp">
                    <SocialDrawer />
                </div>
            </div>
            <div className="row marg">
                <div className="col-12 colp">
                    <Skyliner user={user} auth={auth.user} username={props.match.params.handle} />
                </div>
            </div>
            <div className="row">
                <div className="col-12">
                    <ProfileNav  tabChange={tabFunc} />
                </div>
            </div>
            <div className="row marg">
                {renderTabs()}
            </div>
        </div>
    );
}

export default Profile;
