import React, { useContext } from 'react';

import { AppContext } from '../../context/AuthContext';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Typography } from '@material-ui/core';

import Image from './imgs/1x/background.png';

const useStyles = makeStyles(theme => ({
    container: {
        [theme.breakpoints.up('lg')]: {
            height: '85vh',
        },
        backgroundImage: `url(${Image})`,
        backgroundSize: 'cover',
        height: '80vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    typo: {
        color: 'white',
    }
}));

const Jumbotron = () => {

    const classes = useStyles();
    const holder = useContext(AppContext);

    const getWidth = () => {
        switch (true) {
            case holder.xs:
                return console.log('xs');
            case holder.sm:
                return console.log('sm');
            case holder.md:
                return console.log('md');
            case holder.lg:
                return console.log('lg');
            default:
                return console.log('xl');
        }
    };

    getWidth();

    return (
        <Grid className={classes.container} item xs={12}>

            <Typography className={classes.typo} variant={holder.sm ? 'h2' : 'h1'} align="center">
                Welcome To My Portfolio
            </Typography>

        </Grid>
    );
};

export default Jumbotron;
