import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {
    Grid,
    Typography,
    Icon,
} from '@material-ui/core';

import Logo from './imgs/Logo.png';

const useStyles = makeStyles((theme) => ({
    container: {
        color: 'white',
        backgroundColor: '#0000a2',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexWrap: 'wrap',
    },
    logo: {
        width: '30%',
        height: 'auto',
        marginTop: 30,
    },
    location: {
        width: '100%',
        marginTop: 40,
    },
    header: {
        width: '100%',
        margin: '30px 0px',
    },
    info: {
        width: '100%',
        marginBottom: 30,
    },
}));

const Contact = () => {

    const classes = useStyles();

    return (
        <Grid className={classes.container} item xs={12}>

            <img
                className={classes.logo}
                src={Logo}
                alt="Heath Banner Logo"
            />

            <Typography
                className={classes.location}
                align="center"
            >
                Located In Charlotte, NC
            </Typography>

            <Typography
                className={classes.header}
                variant="h4"
                align="center"
            >
                Contact Me
            </Typography>

            <Typography
                className={classes.info}
                align="center"
            >
                LinkedIn
            </Typography>

            <Typography
                className={classes.info}
                align="center"
            >
                <Icon>email</Icon>
                heathbanner@outlook.com
            </Typography>

            <Typography
                className={classes.info}
                align="center"
            >
                <Icon>phone_android</Icon>  
                704.942.6721
            </Typography>

        </Grid>
    );
};

export default Contact;
