import React, { Fragment, useEffect, useState } from 'react';

import { COMMUNITY_CHAT, MESSAGE_SENT, MESSAGE_RECIEVED, TYPING } from '../../socket/events';

import SideBar from './SideBar';
import ChatHeading from './chatHeading';
import Messages from './messages/Messages';
import MessageInput from './messages/MessageInput';

function Chat(props) {

    const [chats, setChats] = useState([]);
    const [activeChat, setActiveChat] = useState('');
    const [loaded, setLoaded] = useState(false);

    useEffect(() => {
        if(!loaded) {
            setLoaded(true);
            const { socket } = props;
            socket.emit(COMMUNITY_CHAT, resetChat);
        }
    })

    function resetChat(chat) {
        return addChat(chat, true);
    }

    function addChat(chat, reset) {
        const { socket } = props;
        const newChats = reset ? [chat] : [...chats, chat];
        setChats({chats: newChats})
        setActiveChat({activeChat:reset ? chat : activeChat});
        const messageEvent = `${MESSAGE_RECIEVED}=${chat.id}`;
        const typingEvent = `${TYPING}=${chat.id}`;

        socket.on(typingEvent, updateTypingChat(chat.id));
        socket.on(messageEvent, addMessageToChat(chat.id));
    } 

    function addMessageToChat(chatId) {
        return message => {
            let newChats = chats.map((chat) => {
                if (chat.id === chatId) {
                    chat.messages.push(message);
                }
                return chat;
            });
            setChats({chats: newChats});
        };
    }

    function updateTypingChat(chatId) {
        return ({isTyping, user}) => {
            if(user !== props.user.name) {
                let newChats = chats.map((chat) => {
                    if(chat.id === chatId) {
                        if(isTyping && !chat.typingUsers.includes(user)) {
                            chat.typingUsers.push(user);
                        } else if (!isTyping && chat.typingUser.includes(user)) {
                            chat.typingUsers = chat.typingUsers.filter(u => u !== user)
                        }
                    }
                    return chat;
                })
                setChats({chats: newChats});
            }
        }
    }

    function sendTyping(chatId, message) {
        const { socket } = props;
        socket.emit(MESSAGE_SENT, {chatId, message});
    }

    function updateActiveChat(activeChat) {
        setActiveChat(activeChat);
    }

    return (
        <Fragment>
<div className="container">
				<SideBar
					logout={props.logout}
					chats={chats}
					user={props.user}
					activeChat={activeChat}
					setActiveChat={updateActiveChat}
					/>
				<div className="chat-room-container">
					{
						activeChat !== null ? (

							<div className="chat-room">
								<ChatHeading name={activeChat.name} />
								<Messages 
									messages={activeChat.messages}
									user={props.user}
									typingUsers={activeChat.typingUsers}
                                />
								<MessageInput 
									sendMessage={
										(message)=>{
											this.sendMessage(activeChat.id, message)
										}
									}
									sendTyping={
										(isTyping)=>{
											this.sendTyping(activeChat.id, isTyping)
										}
									}
                                />

							</div>
						):
						<div className="chat-room choose">
							<h3>Choose a chat!</h3>
						</div>
					}
				</div>

			</div>
        </Fragment>
    );
}

export default Chat;
