import React, { Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Avatar, Typography, Fab, Icon } from '@material-ui/core';

import { Parallax } from 'react-parallax';

const useStyles = makeStyles({
    skyliner: {
        width: '100%',
        height: '40vh',
    },
    skylinerRow: {
        height: '40vh',
        display: 'flex',
        alignItems: 'flex-end',
    },
    skylinerContent: {
        display: 'flex',
        alignItems: 'flex-end',
        flexDirection: 'row',
        flexWrap: 'none',
    },
    profileAvatar: {
        width: 120,
        height: 120,
        margin: '0px 20px 0px 10px',
    },
    username: {
        color: 'white',
    },
    skylinerDivLeft: {
        display: 'flex',
        alignItems: 'flex-end',
    },
    skylinerDivRight: {
        padding: '0px 20px',
        width: '100%',
        display: 'flex',
        justifyContent: 'flex-end'
    },
    Fab: {
        margin: '10px 20px',
    },
})

function Skyliner(props) {

    const classes = useStyles();

    function renderButtons() {
        if(props.user) {
            if(props.user.username === props.username) {
                return (
                    <Fragment>
                        <Fab className={classes.Fab}>
                            <Icon>person_add</Icon>
                        </Fab>
                        <Fab className={classes.Fab}>
                            <Icon>message</Icon>
                        </Fab>
                    </Fragment>
                );
            }
        }
    }

    return (
        <Fragment>
            <Parallax
                bgImage={props.user.skyline}
                bgImageAlt="Skyliner"
                strength={200}
                className={classes.skyliner}
            >
                <div className={`row ${classes.skylinerRow}`}>
                    <div className={`col-6 ${classes.skylinerContent}`}>
                        <div className={classes.skylinerDivLeft}>
                            <Avatar src={props.user.avatar} className={classes.profileAvatar} />
                            <Typography
                                display="inline"
                                variant="h2"
                                color="textPrimary"
                                className={classes.username}
                            >
                                {props.username}
                            </Typography>
                        </div>
                    </div>
                    <div className={`col-6 ${classes.skylinerContent}`}>
                        <div className={classes.skylinerDivRight}>
                            {renderButtons()}                            
                        </div>
                    </div>
                </div>
            </Parallax>
        </Fragment>
    );
}


export default Skyliner;
