import React, { useEffect, useState } from 'react';
import { Parallax } from 'react-parallax';
import WeatherModule from './WeatherModule';
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';


const useStyles = makeStyles(theme => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
      backgroundColor: theme.palette.background.paper,
      height: '100%'
    },
    gridList: {
      flexWrap: 'nowrap',
      // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
      transform: 'translateZ(0)',
    },
    title: {
      color: theme.palette.primary.light,
    },
    titleBar: {
      background:
        'linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
    },
  }));


function Weather() {

    const [weather, setWeather] = useState('');
    const [isLoaded, setIsLoaded] = useState(false);
    const [sentRequest, setSentRequest] = useState(false);
    const [parsedForecast, setParsedForecast] = useState(null);

    const classes = useStyles();


    useEffect(() => {
        if (!sentRequest) {
            setSentRequest(true)
            fetch('http://api.openweathermap.org/data/2.5/forecast?lat=35&lon=139&APPID=4216d1350fe31af9bf5100bb34fa72e2')
            .then(res => res.json())
            .then((result) => { console.log(result); setWeather(result); setSentRequest(true); });
        }
    })

    if (isLoaded) {
        var dt = '';
        const filteredResult = weather.list.filter(object => {
            console.log(object.dt_txt)
            if(dt === '') {dt = object.dt_txt.split(' '); dt = dt[0]; return true}
            const sample = object.dt_txt.split(' ')
            return !dt === sample[0]
        })
        setParsedForecast(filteredResult);
    }

    var weatherInfo = [];

    if (parsedForecast) {

        weatherInfo = parsedForecast.map(object => {
            return (
            <GridListTile >
                <WeatherModule 
                    weather={object.weather[0].description}
                    image={object.weather[0].icon}
                    temp_max={object.main.temp_max}
                    temp_min={object.main.temp_min}
                    city={weather.city.name}
                    country={weather.city.country}
                />
            </GridListTile>
            )
        })
    } 


    return (
        <div>
            <Parallax
                bgImage={require('./imgs/rowan-heuvel.png')}
                bgImageAlt="Weather"
                strength={400}
            >
                <div className="weather-section">
                    <div className="row">
                        <div className="col-12">
                            <h1 id="weather-header">Weather Concept</h1>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-6">
                            <div id="weather-info">
                                <h3>Description</h3>
                                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
                                    Fusce sed elit nec nulla finibus vestibulum. Duis posuere 
                                    ligula elit, id ornare turpis scelerisque eu. Curabitur 
                                    id gravida velit, nec condimentum diam. Curabitur mattis, 
                                    nisl vel accumsan porttitor, nisl diam ullamcorper leo, 
                                    vel luctus justo sapien et turpis. Curabitur mattis justo 
                                    massa, volutpat hendrerit nisi consequat id. Nulla auctor, 
                                    sapien a posuere rutrum, lectus ex fermentum enim, et 
                                    eleifend ipsum erat in tellus. Aenean dignissim porta porttitor. 
                                    Curabitur at tempus ante. Aliquam malesuada velit in leo 
                                    mollis, in eleifend odio auctor.
                                </p>
                            </div>
                        </div>
                        <div className="col-6">
                            <div id="weather-module">

                            <GridList cellHeight={'auto'} className={classes.gridList} cols={1.7}>
                                {weatherInfo}
                            </GridList>

                            </div>
                        </div>
                    </div>
                </div>
            </Parallax>
        </div>
    );
}

export default Weather;
