import React, { useEffect, useState } from 'react';

import SocialDrawer from '../../components/Navigation/SocialDrawer';
import Skyliner from '../../components/Social/Profile/Skyliner';
import ProfileNav from '../../components/Social/Profile/ProfileNav';
import Story from '../../components/Social/Story/Story';
import Timeline from '../../components/Social/Story/Timeline';

function Profile(props) {

    const [user, setUser] = React.useState('');
    const [onLoad, setOnLoad] = React.useState(false);
    const [tabFunc, setTabFunc] = React.useState({
        onChange: handleTabChange
    });
    const [Timeline, setTimeline] = React.useState(false);
    const [About, setAbout] = React.useState(false);
    const [Friends, setFriends] = React.useState(false);
    const [Photos, setPhotos] = React.useState(false);

    function handleTabChange(tab) {
        console.log(tab)
        switch (tab) {
            case 'Timeline':
                setTimeline(!Timeline);
                break;
            case 'About':
                setAbout(!About);
                break;
            case 'Friends':
                setFriends(!Friends);
                break;
            case 'Photos': 
                setPhotos(!Photos);
                break;
            default:
                break; 
        }

    };

    useEffect(() => {
        const { handle } = props.match.params
        setUser(handle);
        setOnLoad(true);
        if(!onLoad){
            fetch('/api/social/getProfile', {
                method: 'POST',
                body: JSON.stringify({username: handle}),
                headers: {'Content-Type': 'application/json'}
            }).then(res => res.json())
            .then((user) => {
                console.log(user);
            });
        }
    })

    return (
        <div className="container-fluid colp">
            <div className="row marg">
                <div className="col-12 colp">
                    <SocialDrawer />
                </div>
            </div>
            <div className="row marg">
                <div className="col-12 colp">
                    <Skyliner user={user} skyliner="http://getwallpapers.com/wallpaper/full/6/8/c/326021.jpg" />
                </div>
            </div>
            <div className="row">
                <div className="col-12">
                    <ProfileNav  tabChange={tabFunc} />
                </div>
            </div>
            <div className="row marg">
                <div className="col-3 colp">

                </div>
                <div className="col-9 colp">
                    <Story />
                    <Timeline />
                </div>
            </div>
        </div>
    );
}

export default Profile;
