import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {
    Grid,
    Typography,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    container: {
        backgroundColor: '#0000a2',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexWrap: 'wrap',
    },
    why: {
        color: 'white',
    },
}));

const Why = () => {

    const classes = useStyles();

    return (
        <Grid className={classes.container} container>

            {/* <Typography
                className={classes.why}
                variant="h4"
                align="center"
            >
                Why Heath?
            </Typography> */}

            <Grid
                className={classes.whyContainers}
                xs={12}
                sm={4}
                item
            >
                <Typography
                    variant="h5"
                >
                    Experience
                </Typography>
                <Typography>
                    Software development best practices applied on over 500 
                    products in London, Paris and New York
                </Typography>
            </Grid>
            <Grid
                className={classes.whyContainers}
                xs={12}
                sm={4}
                item
            >
                <Typography
                    variant="h5"
                >
                    Time To Market
                </Typography>
                <Typography>
                    Launch new software in weeks, not months
                </Typography>
            </Grid>
            <Grid
                className={classes.whyContainers}
                xs={12}
                sm={4}
                item
            >
                <Typography
                    variant="h5"
                >
                    Continuous Improvement
                </Typography>
                <Typography>
                    Embracing Lean and Kaizen, the Japanese practices for 
                    continuous improvement, allows us to release quickly without 
                    compromising quality
                </Typography>
            </Grid>

        </Grid>
    );
};

export default Why;
