import React from 'react';
import { Link } from 'react-router-dom';

import { makeStyles } from '@material-ui/core/styles';
import { Card, CardHeader, CardContent, Typography, Paper, Button } from '@material-ui/core';

import Tilt from "react-tilt";

const useStyles = makeStyles(theme => ({
  card: {
    maxWidth: 345,
    margin: '50px auto',
    backgroundColor: 'rgb(235, 235, 235)'
  },
  tilt: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  media: {
    height: 100,
    width: 100,
  },
  title: {
    color: 'white'
  },
  paper: {
    backgroundColor: 'rgb(255, 140, 0)',
    padding: '10px'
  },
  button: {
    margin: '10px 0px',
  },
  link: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  }
}));

export default function RecipeReviewCard(props) {
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  function handleExpandClick() {
    setExpanded(!expanded);
  }

  return (
    <Card className={classes.card}>

      <CardHeader
        title={
          <Paper className={classes.paper}>
            <Typography className={classes.title} variant="h5" align="center" >
              {props.app.title}
            </Typography>
          </Paper>
        }
      />

      <Tilt className={classes.tilt}>
          <img src={props.app.image} className={classes.media} />
      </Tilt>

      <CardContent>
        <Typography variant="body2" color="textSecondary" align="center" component="p">
          {props.app.description}
        </Typography>
        <Link to="/" className={classes.link}>
          <Button className={classes.button}>
            Visit
          </Button>
        </Link>
      </CardContent>

    </Card>
  );
}
