import React, { Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Typography, GridList, GridListTile, GridListTileBar, ListSubheader, IconButton, Icon } from '@material-ui/core';

import Background from './imgs/1x/skills.png';

import ReactLogo from './imgs/React.png';
import Material from './imgs/MaterialUI.png';
import Mongo from './imgs/mongodb.png';
import MySQL from './imgs/mysql.svg';
import HandleBars from './imgs/handlebars.png';
import Passport from './imgs/passport.png';
import Node from './imgs/nodejs.png';
import JQuery from './imgs/jquery.png';
import HTML from './imgs/html.png';
import CSS from './imgs/css.png';
import GitHub from './imgs/github.svg';
import Bash from './imgs/bash.png';


const tileData = [
    {
        img: ReactLogo,
        title: 'React JS',
    },
    {
        img: Material,
        title: 'Material UI',
    },
    {
        img: Mongo,
        title: 'Mongo DB'   
    },
    {
        img: MySQL,
        title: 'MySQL'
    },
    {
        img: HandleBars,
        title: 'Handle Bars'
    },
    {
        img: Passport,
        title: 'Passport'
    },
    {
        img: Node,
        title: 'Node JS'
    },
    {
        img: JQuery,
        title: 'JQuery'
    },
    {
        img: HTML,
        title: 'HTML 5'
    },
    {
        img: CSS,
        title: 'CSS 3'
    },
    {
        img: GitHub,
        title: 'Git Hub'
    },
    {
        img: Bash,
        title: 'Bash'
    }
];

const useStyles = makeStyles(theme => ({
    container: {
        backgroundImage: `url(${Background})`,
        backgroundSize: 'cover',
        height: '50vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    header: {
        marginTop: 40,
    },
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        overflowY: 'none',
        // backgroundColor: theme.palette.background.paper,
    },
    gridList: {
        width: 500,
        height: 450,
        overflowY: 'visible',
    },
    tile: {
    },
    icon: {
        color: 'rgba(255, 255, 255, 0.54)'
    },
}));

const Skills = () => {

    const classes = useStyles();

    return (

        <Fragment>

            <Grid className={classes.container} item xs={12}>

                <Typography className={classes.header} variant="h1">
                    What Do I Know?
                </Typography>

            </Grid>
            <Grid item xs={12}>

                <div className={classes.root}>

                    <GridList cellHeight={180} cols={3} className={classes.gridList}>

                        {
                            tileData.map(tile => (

                                <GridListTile key={tile.img}>

                                    <img src={tile.img} alt={tile.title} />

                                    <GridListTileBar
                                        title={tile.title}
                                        actionIcon={
                                            <IconButton aria-label={`info about ${tile.title}`} className={classes.icon}>
                                                <Icon>info</Icon>
                                            </IconButton>
                                        }
                                    />

                                </GridListTile>
                            ))
                        }

                    </GridList>

                </div>

            </Grid>

        </Fragment>
    );
};

export default Skills;
