import React, {useState, useContext} from 'react';

import { CTX } from '../../../context/Story';

import { makeStyles } from '@material-ui/core/styles';
import { Typography, List, ListItem, Icon, TextField, Button, Paper, Chip } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
    chatMenu: {
        position: 'fixed',
        bottom: '0',
        right: '0',
    },
    chatPaper: {        
        minHeight: 300,
        minWidth: 200,
        padding: '10px 10px',

    },
    chatInput: {
        position: 'absolute',
        bottom: 0,
        display: 'flex',
        margin: '0px 10px',
    },
    chatList: {
        display: 'flex',
        justifyContent: 'flex-start',
        paddingInlineStart: '0px'
    },   
    listItem: {
        padding: '0px 0px 0px 0px'
    },
}))


function Chat() {
    const { allChats, sendChatAction, user } = useContext(CTX);
    const topics = Object.keys(allChats);

    const [textValue, setTextValue] = useState('');
    const [activeTopic, setActiveTopic] = useState(topics[0]);

    const classes = useStyles();


    function handleChange(e) {
        const { value } = e.target;
        setTextValue(value);
    }


    return (

        <Paper className={classes.chatPaper}>
        <Typography align="center">
            Chat
        </Typography>
        <List>
            {allChats[activeTopic].map((chat, index) => {
                return(
                    <ListItem key={index} className={classes.listItem}>
                        <Chip 
                            icon={<Icon>message</Icon>}
                            label={`${chat.from}: ${chat.msg}`}
                        />
                    </ListItem>
                )
            })}
        </List>

        <List className={classes.chatList}>

        </List>

        <div className={classes.chatInput}>
            <TextField 
                variant="outlined"
                value={textValue}
                onChange={handleChange}
            />
            <Button onClick={() => {sendChatAction({from: user, msg: textValue, topic: activeTopic}); setTextValue('');}}>Send</Button>
        </div>
    </Paper>
    ); 
}

export default Chat;
