import React, { Fragment, useContext } from 'react';
import AuthContext from '../../../context/AuthContext';


import { makeStyles } from '@material-ui/core/styles';
import { Icon } from '@material-ui/core';

function SideBar(props) {

    const { chats, activeChat, setActiveChat} = props;
	console.log(auth.user)

	const auth = useContext(AuthContext);

	function renderSideBar() {
		// if(auth.user) {
			if (auth.user) {
				return (
				<Fragment>
				<div id="side-bar">
						<div className="heading">
							<div className="app-name">Our Cool Chat <Icon>chevron_down</Icon></div>
							<div className="menu">
								<Icon>menu</Icon>
							</div>
						</div>
						<div className="search">
							<i className="search-icon"><Icon>search</Icon></i>
							<input placeholder="Search" type="text"/>
							<div className="plus"></div>
						</div>
						<div 
							className="users" 
							// ref='users' 
							onClick={(e)=>{ (e.target === auth.user) && setActiveChat(null) }}>

							{
							chats.map((chat)=>{
								if(chat.name){
									const lastMessage = chat.messages[chat.messages.length - 1];
									const user = chat.users.find(({name})=>{
										return name !== this.props.name
									}) || { name:"Community" }
									const classNames = (activeChat && activeChat.id === chat.id) ? 'active' : ''

									return(
									<div 
										key={chat.id} 
										className={`user ${classNames}`}
										onClick={ ()=>{ setActiveChat(chat) } }
										>
										<div className="user-photo">{auth.user.username[0].toUpperCase()}</div>
										<div className="user-info">
											<div className="name">{auth.user.username}</div>
											{lastMessage && <div className="last-message">{lastMessage.message}</div>}
										</div>

									</div>
								)
								}

								return null
							})	
							}

						</div>
						<div className="current-user">
							<span>{auth.user.username}</span>
							{/* <div onClick={()=>{logout()}} title="Logout" className="logout">
								<Icon>eject</Icon>	
							</div> */}
						</div>
				</div>
			</Fragment>
				)
			}

		}
	// }

    return (
		<Fragment>
			{renderSideBar()}
		</Fragment>
	);
}

export default SideBar;
