import React, { useContext, Fragment, useState } from 'react';
import AuthContext from '../../../context/AuthContext';
import Moment from 'react-moment';

import { makeStyles } from '@material-ui/core/styles';
import { TextField, Collapse, List, ListItem, ListItemText, ListItemAvatar, Divider, Typography, Avatar, Icon, IconButton} from '@material-ui/core';

import GetMoment from 'moment';

const useStyles = makeStyles(theme => ({
    root: {
        backgroundColor: theme.palette.background.paper,
        paddingTop: '0px',
        paddingBottom: '0px',
    },
    form: {
        display: 'flex',
        alignItems: 'center',
    }
}));

function Timeline() {

    const classes = useStyles();
    const auth = useContext(AuthContext);

    const [openComment, setOpenComment] = useState(true);
    const [commentInput, setCommentInput] = useState('');

    const [storyIDs, setStoryIDs] = useState({});
    const [friendStories, setFriendStories] = useState(false);
    const [onLoad, setOnLoad] = useState(false);

    if((!onLoad) && (auth.user)){
        console.log(auth.user.info);
        setOnLoad(true);
        fetch('/api/social/friendStories', {
            method: 'POST',
            body: JSON.stringify({id: auth.user.info}),
            headers: {'Content-Type': 'application/json'}
        }).then(res => res.json())
        .then((result) => {
            var storyStorage = result;
            var storyIDList = {};
            auth.user.info.authored_stories.map((story, index) => {
                storyIDList[story._id] = false
                storyStorage.push(story);
            });
            setFriendStories(storyStorage);
            setStoryIDs(storyIDList)
        })
    }

    function handleDelete(id) {
        console.log(id);
        fetch('/api/social/deleteStory', {
            method: 'POST',
            body: JSON.stringify({id: id}),
            headers: {'Content-Type': 'application/json'}
        }).then(res => res.json())
        .then((user) => {
            console.log(user);
            auth.updateAuth(user);
        })
    }

    function handleCommentInput(e) {
        const { name, value } = e.target;
        setCommentInput(value);
    }

    function handleCommentSubmit(e, id) {
        e.preventDefault();
        const {username } = auth.user;
        const time = GetMoment();
        console.log(id, commentInput);
        fetch('api/social/newComment', {
            method: 'POST',
            body: JSON.stringify({id: id, comment: commentInput, username: username, time: time}),
            headers: {'Content-Type': 'application/json'}
        }).then(res => res.json())
        .then((user) => {
            console.log(user);
        });
    };

    function renderStories() {

        if(friendStories) {
            return (
                friendStories.map((story, index) => {
                    return (
                        <Fragment key={story._id}>
                            <ListItem>
                                <ListItemAvatar><Avatar alt={story._id} src="/imgs/avatar.jpg" /></ListItemAvatar>
                                <ListItemText 
                                    primary={story.text} 
                                    secondary={
                                        <Moment format={'dddd h:mm a'}>{story.time}</Moment>
                                    }
                                >
                                </ListItemText>
                                <IconButton onClick={() => expandComment(story._id)}>
                                    <Icon>{storyIDs ? storyIDs[story._id] ? 'expand_less' : 'expand_more' : false}</Icon>
                                </IconButton>
                                <IconButton onClick={() => handleDelete(story._id)}>
                                    <Icon>delete_outline</Icon>
                                </IconButton>
                            </ListItem>
                            <Collapse in={storyIDs ? storyIDs[story._id] : false} timeout="auto" unmountOnExit>
                                <List disablePadding>
                                    {renderComments(story.comments)}
                                </List>
                                <form className={classes.form} onSubmit={(e) => handleCommentSubmit(e, story._id)}>
                                    <TextField
                                        name='123'
                                        value={commentInput}
                                        variant="outlined"
                                        label="Add a comment"
                                        onChange={handleCommentInput}
                                    />

                                    <IconButton type="submit">
                                        <Icon>add_circle</Icon>
                                    </IconButton>
                                </form>
                            </Collapse>
                            <Divider />
                        </Fragment>
                    );
                })
            );
        }
    };

    function renderComments(comments) {
        var commentList = [];
        if(comments){
            if(comments.length < 5){commentList = comments.slice(0, comments.length)}
            else{commentList = comments.slice(0, 5)}
            return (
                commentList.map((comment, index) => {
                    return (  
                        <ListItem key={index}>
                            <ListItemText primary={`${comment.username}: ${comment.text}`} secondary={<Moment date={comment.time} format={'dddd h:mm a'} />} />
                        </ListItem>
                    );
                })
            );
        }

    };

    function expandComment(id) {
        const newCommentState = !storyIDs[id];
        setStoryIDs({...storyIDs, [id]: newCommentState});
    }

    return (
        <div>
            <List className={classes.root}>
                {renderStories()}
            </List>
        </div>
    );
}

export default Timeline;
