import React, { Fragment, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Stepper, Step, StepLabel, Button, Typography, InputLabel, Input, FormControl, TextField } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
    root: {
        width: '70%',
        position: 'absolute',
        top: '50%',
        left: '50%',
        // transform: 'translate(-50%, -50%)'
    },
    button: {
        marginRight: theme.spacing(1),
    },
    instructions: {
        marginTop: theme.spacing(1),
        marginBottom: theme.spacing(1),
    },
    inputDiv: {
        display: 'flex',
        justifyContent: 'center',
    }
}));

function getSteps() {
    return ['Confirm Password', 'Change Email'];
}

    function getStepContent(step) {
        console.log(step)
        switch (step) {
            case 0:
                return 'Authentication';
            case 1: 
                return 'Enter your new email';
            default:
                return 'Unknown step...';
        }
    }

function EditEmail() {

    const [activeStep, setActiveStep] = React.useState(0);
    const [passwordAuth, setPasswordAuth] = React.useState(false);
    const [passwordInput, setPasswordInput] = React.useState('');
    const [confirmInput, setConfirmInput] = React.useState('');    

    const steps = getSteps();
    const classes = useStyles();

    function handleNext() {
        setActiveStep(prevActiveStep => prevActiveStep + 1);
    }

    function handleBack() {
        setActiveStep(prevActiveStep => prevActiveStep - 1);
    }

    function handleReset() {
        setActiveStep(0);
    }

    function handlePassword(e) {
        e.preventDefault();
        console.log(e.target.value);
    }

    function handleInputChange(e) {
        const { name, value } = e.target
        console.log(name, value);
        // if(input === 'password'){setPasswordInput()}
    }

    return (
        <div className={classes.root}>
            <Stepper activeStep={activeStep}>
                {steps.map((label, index) => {
                    const stepProps = {};
                    const labelProps = {};
                    return (
                        <Step key={label} {...stepProps}>
                            <StepLabel {...labelProps}>{label}</StepLabel>
                        </Step>
                    );
                })}
            </Stepper>
            <div className={classes.inputDiv}>
            {activeStep === steps.length ? (
                    <div>
                        <Typography className={classes.instructions}>
                            All steps completed
                        </Typography>
                        <Button onClick={handleReset} className={classes.button}>
                            Reset
                        </Button>
                    </div>
                ): (
                    <div>
                        <Typography className={classes.instructions}>
                            {getStepContent(activeStep)}
                        </Typography>
                        <form onSubmit={handlePassword}>
                            <TextField required label="Enter Password" onChange={handleInputChange} value={passwordInput} name="password" variant="outlined" /> 
                            <TextField required label="Confirm Password" onChange={handleInputChange} value={confirmInput} name="password" variant="outlined"  />
                            <Button type="submit">
                                Submit
                            </Button>
                        </form>
                    <div>
                    <Button disabled={activeStep === 0} onClick={handleBack} className={classes.button}>
                        Back
                    </Button>
                    <Button disabled={!passwordAuth} variant="contained" color="primary" onClick={handleNext} className={classes.button}>
                        {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                    </Button>
                    </div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default EditEmail;
