import TokenStore from '../lib/TokenStore';

function handleLogin(user, authToken) {
    TokenStore.setToken(authToken);
    newState(prevState => ({auth: {...prevState.auth, user, authToken} }));
    username = user;
  }

  function handleLogout(user, authToken) {
    TokenStore.clearToken();
    setAuth(prevState => ({auth: {...prevState.auth, user: undefined, authToken: undefined} }));
  }


const initialState = {
    username: '',
    user: undefined,
    authToken: TokenStore.getToken(),
    onLogin: handleLogin,
    onLogout: handleLogout,
};

const reducer = (state = initialState, action) => {
    const newState = {...state};
    console.log(action);
    if(action.type==="USER"){
        newState.username = action.val
    }

    return newState;
};

export default reducer;
