import React, { useEffect, useState } from 'react';
import { Parallax } from 'react-parallax';
import WeatherModule from './WeatherModule';

function Weather() {

    const [weather, setWeather] = useState('');
    const [isLoaded, setIsLoaded] = useState(false);

    useEffect(() => {
        if (!isLoaded) {
            fetch('https://api.openweathermap.org/data/2.5/weather?id=4460243&APPID=4216d1350fe31af9bf5100bb34fa72e2')
            .then(res => res.json())
            .then((result) => {
                console.log(result);
                setWeather(result);
                setIsLoaded(true)
            });
        }
    })


    return (
        <div>
            <Parallax
                bgImage={require('./imgs/rowan-heuvel.png')}
                bgImageAlt="Weather"
                strength={400}
            >
                <div className="weather-section">
                    <div className="row">
                        <div className="col-12">
                            <h1 id="weather-header">Weather Concept</h1>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-6">
                            <div id="weather-info">
                                <h3>Description</h3>
                                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
                                    Fusce sed elit nec nulla finibus vestibulum. Duis posuere 
                                    ligula elit, id ornare turpis scelerisque eu. Curabitur 
                                    id gravida velit, nec condimentum diam. Curabitur mattis, 
                                    nisl vel accumsan porttitor, nisl diam ullamcorper leo, 
                                    vel luctus justo sapien et turpis. Curabitur mattis justo 
                                    massa, volutpat hendrerit nisi consequat id. Nulla auctor, 
                                    sapien a posuere rutrum, lectus ex fermentum enim, et 
                                    eleifend ipsum erat in tellus. Aenean dignissim porta porttitor. 
                                    Curabitur at tempus ante. Aliquam malesuada velit in leo 
                                    mollis, in eleifend odio auctor.
                                </p>
                            </div>
                        </div>
                        <div className="col-6">
                            <div id="weather-module">
                                {isLoaded ? <WeatherModule 
                                    weather={weather.weather[0].description}
                                    image={weather.weather[0].icon}
                                    temp_max={weather.main.temp_max}
                                    temp_min={weather.main.temp_min}
                                /> : console.log()}
                            </div>
                        </div>
                    </div>
                </div>
            </Parallax>
        </div>
    );
}

export default Weather;
