import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import Tilt from 'react-tilt';
import Moment from 'react-moment';
import 'moment-timezone';
import FormatDate from 'moment';
import { textAlign } from '@material-ui/system';

const useStyles = makeStyles(theme => ({
    root: {
        width: '70%',
        margin: '50px auto',
        padding: '20px 20px',
        width: '85%'
    },
    weather: {
        textTransform: 'capitalize',
        marginTop: '10px'
    },
    city: {
        display: 'flex',
        alignItems: 'center',
    },
    date: {
        display: 'flex',
        flexWrap: 'wrap-reverse',
        alignItems: 'center',
    }
}));


function WeatherModule(props) {
    const classes = useStyles();
    const image =  `https://openweathermap.org/img/w/${props.image}.png`
    var date = FormatDate(props.date)


    return (
        <div>
            <Paper className={classes.root}>
                <div className="row">
                    <div className="col-6">
                        <div className={classes.city}>
                            <Typography variant="h6">
                                {props.city}
                            </Typography>
                        </div>
                    </div>
                    <div className="col-6">
                        <div className={classes.date}>
                            <Moment date={date} interval={30000} format="dddd MMM YY" />
                        </div>
                    </div>
                </div>
                <div className="row">
                    <div className="col-12">
                        <Typography variant="h6" className={classes.weather}>
                            {props.weather}
                        </Typography>
                    </div>
                </div>
                <div className="row">
                    <div className="col-12">
                        <Tilt>
                            <img src={image} alt={props.weather} />
                        </Tilt>
                    </div>
                </div>
                <div className="row">
                    <div className="col-12">
                        <Typography variant="h6">
                            High: {props.temp_max}
                        </Typography>
                        <Typography>
                            Low: {props.temp_min}
                        </Typography>
                    </div>
                </div>
            </Paper>
        </div>
    );
}

export default WeatherModule;
