import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Card, CardHeader, CardContent, Avatar, Icon, IconButton, Typography } from '@material-ui/core';

import Tilt from "react-tilt";

const useStyles = makeStyles(theme => ({
  card: {
    maxWidth: 345,
    margin: '50px auto',

  },
  media: {
    height: '100px',
    width: '100px'
    // paddingTop: '56.25%', // 16:9
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: 'rgb(0, 155, 28)',
  },
  title: {
      fontWeight: 'bold',
      fontSize: '22px'
  }
}));

export default function RecipeReviewCard(props) {
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  function handleExpandClick() {
    setExpanded(!expanded);
  }

  return (
    <Card className={classes.card}>
      <CardHeader
        avatar={
          <Avatar aria-label="Recipe" className={classes.avatar}>
            <Icon>filter_vintage</Icon>
          </Avatar>
        }
        action={
          <IconButton aria-label="Settings">
            <Icon>more_vert</Icon>
          </IconButton>
        }
        title={props.app.title}
        classes={{title: classes.title}}
      />
      <Tilt>
          <img src={props.app.image} className={classes.media} />
      </Tilt>
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          {props.app.description}
        </Typography>
      </CardContent>
    </Card>
  );
}
