import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Grid, Paper, Typography, Divider } from '@material-ui/core';

import Tilt from 'react-tilt';
import Moment from 'react-moment';
import 'moment-timezone';
import FormatDate from 'moment';

const useStyles = makeStyles(theme => ({
    moduleContainer: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    moduleBox: {
        width: '70%',
        backgroundColor: 'rgb(0, 0, 0, 0.1)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
    },
    weatherBoxes: {
        width: '33.3%',
        height: 150,
        padding: 20,
        borderRadius: 0,
        backgroundColor: 'rgb(0, 0, 0, 0.1)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
    },
    boxHeader: {
        textTransform: 'capitalize',
        width: '100%'
    },
}));


const WeatherModule = (props) => {

    const classes = useStyles();

    const image =  `https://openweathermap.org/img/w/${props.image}.png`
    var date = FormatDate(props.date)

    return (
        <Grid className={classes.moduleContainer} item xs={12}>

            <Paper className={classes.moduleBox}>

                <Typography style={{ width: '100%', padding: 20 }} variant="h1" align="center">
                    {props.city}
                </Typography>

                <Divider style={{ width: '70%', marginBottom: 20 }} />

                {
                    props.forecast.map((weather) => (
                        <Paper className={classes.weatherBoxes}>

                            <Typography className={classes.boxHeader} variant="h4" align="center">
                                {weather.weather[0].description}
                            </Typography>
                            <img
                                src={`https://openweathermap.org/img/w/${weather.weather[0].icon}.png`}
                                alt="Weather"
                            />
                            <Typography style={{ width: '100%' }} variant="h5" align="center">
                                High: {`${Math.round(weather.main.temp_max)}`}&#8457;
                            </Typography>
                            <Typography style={{ width: '100%' }} variant="h6" align="center">
                                Low: {`${Math.round(weather.main.temp_min)}`}&#8457;
                            </Typography>

                        </Paper>
                    ))
                }                

            </Paper>

        </Grid>
    );
}

export default WeatherModule;
