import React, { useContext } from 'react';
import { Link } from 'react-router-dom';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Button, Divider, Typography, Paper, Avatar } from '@material-ui/core';

import { EditorContext } from '../../../../context/EditorContext';

const useStyles = makeStyles((theme) => ({
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent:'center',
        flexWrap: 'wrap',
    },
    button: {
        width: '100%',
        padding: 10,
        color: 'white',
        backgroundColor: 'rgb(0, 0, 0, 0.7)',
        transition: 'background-color 0.4s ease',
        '&:hover': {
            backgroundColor: 'rgb(0 , 0, 0, 1)',
        },
    },
    paper: {
        width: '70%',
        marginTop: 40,
        padding: 30,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexWrap: 'wrap',
        cursor: 'pointer',
    },
    infoContainer: {
        position: 'relative',
        height: 60,
        marginTop: 20,
        paddingLeft: 80,
        display: 'inline-flex',
        justifyContent: 'flex-start',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
        flexBasis: 'auto',
    },
    avatar: {
        height: 60,
        width: 60,
        position: 'absolute',
        top: 0,
        left: 0,
    },
}));

const ArticleSelection = () => {

    const holder = useContext(EditorContext);
    const classes = useStyles();

    return (
        <Grid container>

            <Grid className={classes.container} item xs={12}>

                <Link 
                    style={{
                        textDecoration: 'none',
                        color: 'inherit',
                        width: '70%',
                        marginTop: 30,
                    }}
                    to={`/editor/new`}
                >
                    <Button className={classes.button}>
                        <Typography>
                            Make New Article
                        </Typography>
                    </Button>
                </Link>

            </Grid>

            <Divider style={{width: '100%', marginTop: 40}} />

            <Grid className={classes.container} item xs={12}>

                {
                    holder.articleList
                        ?
                    holder.articleList.map((article, index) => {
                        return (
                            <Paper
                                className={classes.paper}
                                key={article.title.text}
                            >

                                <Typography style={{ width: '100%', marginBottom: 20 }} align="center" variant="h2">
                                    <Link style={{ textDecoration: 'none', color: 'inherit' }} to={`/editor/${article.title.text}`}>
                                        {article.title.text}
                                    </Link>
                                </Typography>

                                <Typography style={{ width: '100%'}} align="center" variant="h5" color="textSecondary">
                                    <Link style={{ textDecoration: 'none', color: 'inherit' }} to={`/editor/${article.title.text}`}>
                                        {article.description.text}
                                    </Link>
                                </Typography>

                                <div className={classes.infoContainer} style={{ width: 'auto' }}>

                                    <Avatar
                                        className={classes.avatar}
                                        src={`https://media.licdn.com/dms/image/C5603AQHqTyUqMrqJZA/profile-displayphoto-shrink_100_100/0?e=1572480000&v=beta&t=QNVV8XF1wUl0Pw5sTEVgahCY8A0N1d58dCU7qAXFf20`}
                                        alt="Heath Banner Profile Photo"
                                    />

                                    <div style={{ width: '100%' }}>
                                        <Typography>
                                            Heath Banner
                                        </Typography>
                                    </div>

                                    <Typography color="textSecondary">
                                        {`${article.date.parsedDate} `} &#8226; {` ${article.readLength.text} min read`}
                                    </Typography>

                                </div>

                                <Link
                                    style={{
                                        textDecoration: 'none',
                                        color: 'inherit',
                                        width: '100%',
                                    }}
                                    to={`/editor/${article.title.text}`}
                                >
                                    <div
                                        style={{
                                            width: '100%',
                                            display: 'flex',
                                            justifyContent: 'center',
                                        }}
                                    >
                                        <img
                                            style={{
                                                marginTop: 10,
                                                marginBottom: 10,
                                                width: '50%',
                                            }}
                                            src={article.jumbotron.src}
                                            alt={article.title.text}
                                        />
                                    </div>
                                </Link>

                            </Paper>
                        )
                    })
                        :

                    ''
                }

            </Grid>

        </Grid>
    );
};

export default ArticleSelection;
