import React, { Fragment, useState } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Avatar, Typography, Paper, Stepper, Step, StepLabel, Button } from '@material-ui/core';

const Photo = 'https://media.licdn.com/dms/image/C5603AQHqTyUqMrqJZA/profile-displayphoto-shrink_200_200/0?e=1569456000&v=beta&t=YtZoiTHVJEXn3syxgpM5VVg7zTMFjD6SXZLVKHh9JB8';

const useStyles = makeStyles(theme => ({
    container: {
        position: 'relative',
        height: 200,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    aboutContainer: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    avatar: {
        height: 200,
        width: 200,
        position: 'absolute',
        top: 20,
        left: 20,
    },
    skillsHeader: {
        color: 'white',
    },
    paper: {
        width: '60%',
    },
    spacer: {
        height: '80vh',
    },
}));

function getSteps() {
    return ['Select campaign settings', 'Create an ad group', 'Create an ad'];
  }

  function getStepContent(step) {
    switch (step) {
      case 0:
        return 'Select campaign settings...';
      case 1:
        return 'What is an ad group anyways?';
      case 2:
        return 'This is the bit I really care about!';
      default:
        return 'Unknown step';
    }
  }

const Introduction = () => {

    const classes = useStyles();

    const [activeStep, setActiveStep] = useState(0);
    const [skipped, setSkipped] = useState(new Set());
    const steps = getSteps();

    function handleNext() {
        let newSkipped = skipped;

        setActiveStep(prevActiveStep => prevActiveStep + 1);
        setSkipped(newSkipped);
      }

      function handleBack() {
        setActiveStep(prevActiveStep => prevActiveStep - 1);
      }

      function handleReset() {
        setActiveStep(0);
      }

    return (
        <Fragment>

            <Grid className={classes.container} item xs={12}>

                <Avatar className={classes.avatar} src={Photo} alt="Heath Banner"  />   

                <Typography className={classes.skillsHeader} variant="h1" align="center">
                    A Little About Me
                </Typography>         

            </Grid>
            <Grid className={classes.aboutContainer} item xs={12}>

                <Paper className={classes.paper}>

                    <Typography variant="h3">
                        Highschool
                    </Typography>

                    <Typography variant="body1">
                        {`I was born and raised in Charlotte, NC. Growing up, I 
                        eventually picked up on technology in highschool. The Xbox360 
                        really ignited the flame within me. Going from a regular gamer 
                        to becoming a moderator on a very famous game modding website
                        (The Tech Game). A friend and I had released the first modded 
                        file for Call of Duty BlackOps Zombies, a week before the game 
                        was even released.`}
                    </Typography>

                </Paper>

            </Grid>


            <Grid item xs={12} className={classes.spacer}>

            <Stepper activeStep={activeStep}>
                {steps.map((label) => {
                const stepProps = {};
                const labelProps = {};
                return (
                    <Step key={label} {...stepProps}>
                    <StepLabel {...labelProps}>{label}</StepLabel>
                    </Step>
                );
                })}
            </Stepper>
      <div>
        {activeStep === steps.length ? (
          <div>
            <Typography className={classes.instructions}>
              All steps completed - you&apos;re finished
            </Typography>
            <Button onClick={handleReset} className={classes.button}>
              Reset
            </Button>
          </div>
        ) : (
          <div>
            <Typography className={classes.instructions}>{getStepContent(activeStep)}</Typography>
            <div>
              <Button disabled={activeStep === 0} onClick={handleBack} className={classes.button}>
                Back
              </Button>

              <Button
                variant="contained"
                color="primary"
                onClick={handleNext}
                className={classes.button}
              >
                {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
              </Button>
            </div>
          </div>
        )}
      </div>

            </Grid>

        </Fragment>
    );
};

export default Introduction;
