import React, { useContext, useState, Fragment } from 'react';
import { Link } from 'react-router-dom';

import { AppContext } from '../../../context/AuthContext';

import clsx from 'clsx';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import { CircularProgress, Tooltip, Zoom, Drawer, Avatar, CssBaseline, AppBar, Toolbar, List, Typography, Divider, IconButton, Icon, ListItem, ListItemIcon, ListItemText } from '@material-ui/core';

import Moment from 'react-moment';
import FormatDate from 'moment';

export default function PersistentDrawerLeft(props) {

  const useStyles = makeStyles(theme => ({
    root: {
      display: 'flex',
      position: 'relative',
      zIndex: 1500,

    },
    appBar: {
      transition: theme.transitions.create(['margin', 'width'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
    },
    appBarShift: {
      [theme.breakpoints.down('md')]: {
        width: `calc(100% - 240px)`,
        marginLeft: '240px',
        transition: theme.transitions.create(['margin', 'width'], {
          easing: theme.transitions.easing.easeOut,
          duration: theme.transitions.duration.enteringScreen,
        }),
      },
      [theme.breakpoints.down('sm')]: {
        width: `calc(100% - 180px)`,
        marginLeft: '180px',
        transition: theme.transitions.create(['margin', 'width'], {
          easing: theme.transitions.easing.easeOut,
          duration: theme.transitions.duration.enteringScreen,
        }),
      },
      [theme.breakpoints.down('xs')]: {
        width: `calc(100% - 140px)`,
        marginLeft: '140px',
        transition: theme.transitions.create(['margin', 'width'], {
          easing: theme.transitions.easing.easeOut,
          duration: theme.transitions.duration.enteringScreen,
        }),
      }
    },
    toolBar: {
      display: 'flex',
      alignItems: 'center',
      flexWrap: 'nowrap',
      background: 'linear-gradient(45deg, #ff9147 30%, #ff7d26 90%)',
    },
    menuButton: {
        position: 'absolute',
        left: '3%',
        marginRight: theme.spacing(2),
        color: props.mood ? `${props.mood.menuButton}` : 'white',
    },
    barTitle: {
      [theme.breakpoints.down('xs')]: {
        top: '50%',
        right: '0%',
        transform: 'translate(-50%, -50%)',
        zIndex: '10',
        padding: '0px 5px',
      },
      position: 'absolute',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      display: 'flex',
      alignItems: 'center',
      color: props.mood ? `${props.mood.barTitle}` : 'white',
      width: 'auto',
    },
    typo: {
      width: 'auto',
      display: 'inline',
      marginLeft: '10px',
    },
    hide: {
      display: 'none',
    },
    drawer: {
      width: '0px',
      flexShrink: 0,
    },
    drawerPaper: {
      [theme.breakpoints.down('md')]:{
        width: 240,
      },
      [theme.breakpoints.down('sm')]:{
        width: 180,
      },
      [theme.breakpoints.down('xs')]:{
        width: 160,
      },
      position: 'absolute',
      zIndex: 1600,
      overflowY: 'visible',
      height: '100vh',
    },
    drawerHeader: {
      display: 'flex',
      alignItems: 'center',
      padding: '0 8px',
      ...theme.mixins.toolbar,
      justifyContent: 'flex-end',
    },
    content: {
      flexGrow: 1,
      padding: theme.spacing(3),
      transition: theme.transitions.create('margin', {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
      [theme.breakpoints.down('md')]:{
        marginLeft: -240,
      },
      [theme.breakpoints.down('sm')]:{
        marginLeft: -180,
      },
      [theme.breakpoints.down('xs')]:{
        marginLeft: -140
      },
    },
    contentShift: {
      transition: theme.transitions.create('margin', {
        easing: theme.transitions.easing.easeOut,
        duration: theme.transitions.duration.enteringScreen,
      }),
      marginLeft: 0,
    },
    moment: {
      [theme.breakpoints.down('xs')]: {
        display: 'none',
      },
      position: 'absolute',
      right: '3%',
      marginRight: theme.spacing(2),
      display: 'flex',
      alignItems: 'center',
    },
    avatar: {
      height: 50,
      width: 50,
    },
    clock: {
      marginRight: '5px'
    },
    link: {
      color: 'black',
      textDecoration: 'none',
      '&:hover': {
        color: 'black',
      }
    },
    listItemIcon: {
      minWidth: '0px !important',
      marginRight: '10px',
    },
  }));

  const drawerList = [
    {
      text: 'Inbox',
      icon: 'inbox',
      link: '/',
      tip: 'This will lead the user to their inbox'
    },
    {
      text: 'Settings',
      icon: 'settings',
      link: '/',
      tip: 'The user will be able to change their email, password, avatar and other settings'
    },
    {
      text: 'Find Friends',
      icon: 'people',
      link: '/',
      tip: `This will lead the user to the friend finder page where they may search for new friends`
    }
  ];

  var drawerWidth;

  const classes = useStyles();
  const theme = useTheme();
  const holder = useContext(AppContext);

  const drawerWidthDynam = () => {
    switch (true) {
      case holder.xs:
        return drawerWidth = 120;
      case holder.sm:
        return drawerWidth = 180;
      default:
        return drawerWidth = 240;
    }
  };

  drawerWidthDynam()


  const [open, setOpen] = useState(false);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const getTime = () => {
    date = FormatDate()
  };

  let date = ''
  setInterval(getTime, 30000);

  return (
      <Fragment>

        <div className={classes.root}>

          <CssBaseline />

          <AppBar
            position="relative"
            className={clsx(classes.appBar, {
              [classes.appBarShift]: open,
            })}
          >

            <Toolbar className={classes.toolBar}>

              <IconButton
                color="inherit"
                aria-label="Open drawer"
                onClick={handleDrawerOpen}
                edge="start"
                className={clsx(classes.menuButton, open && classes.hide)}
              >
                <Icon fontSize={holder.sm ? 'small' : 'large'}>menu</Icon>
              </IconButton>

                <Link className={classes.barTitle} to='/'>

                  <Avatar
                    className={classes.avatar}
                    alt={holder.auth ? holder.auth.username : ''}
                    src={`https://media.licdn.com/dms/image/C5603AQHqTyUqMrqJZA/profile-displayphoto-shrink_200_200/0?e=1571270400&v=beta&t=Fwpkf09f2GNerYN2fC12EwcsHYivKOPTtRBhechPva4`}
                  />

                  <Typography className={classes.typo} variant={holder.sm ? 'h6' : 'h5'}>
                    {holder.auth ? holder.auth.username : <CircularProgress />}
                  </Typography>

                </Link>

              <div className={classes.moment}>

                <Icon fontSize={holder.sm ? 'small' : 'large'} className={classes.clock}>access_time</Icon>

                <Typography variant={holder.sm ? 'body1' : 'h6'}>
                  <Moment interval={30000} date={date} format={'dddd h:mm a'} />
                </Typography>

              </div>

            </Toolbar>

          </AppBar>

          <Drawer
            className={classes.drawer}
            variant="persistent"
            anchor="left"
            open={open}
            classes={{
              paper: classes.drawerPaper,
            }}
          >

            <div className={classes.drawerHeader}>

              <IconButton onClick={handleDrawerClose}>

                {
                  theme.direction === 'ltr'
                    ?
                  <Icon>chevron_left</Icon>
                    :
                  <Icon>cheveron_right</Icon>
                }

              </IconButton>

            </div>

            <Divider />

            <List>

              {
                drawerList.map((item) => (
                  <Tooltip TransitionComponent={Zoom} title={item.tip} key={item.text}>

                    <Link key={item.text} style={{ color: 'black', textDecoration: 'none' }} to={item.link}>

                      <ListItem button>

                        <ListItemIcon className={classes.listItemIcon} >
                          <Icon fontSize={holder.xs ? 'small' : 'large'}>{item.icon}</Icon>
                        </ListItemIcon>

                        <ListItemText
                          primary={
                            <Typography variant={holder.xs ? 'body2' : 'h6'}>
                              {item.text}
                            </Typography>
                          }
                        />

                      </ListItem>

                    </Link>

                  </Tooltip>
                ))
              }

              <Tooltip TransitionComponent={Zoom} title="This button should do what you'd think it would do...if only it were the real website">

                <ListItem button key='Logout'>

                  <ListItemIcon className={classes.listItemIcon}>
                    <Icon fontSize={holder.xs ? 'small' : 'large'}>power_settings_new</Icon>
                  </ListItemIcon>

                  <ListItemText
                    primary={
                      <Typography variant={holder.xs ? 'body2' : 'h6'}>
                        Logout
                      </Typography>
                    }
                  />

                </ListItem>

              </Tooltip>

