import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import Tilt from 'react-tilt';
import Moment from 'react-moment';
import 'moment-timezone';
import FormatDate from 'moment';

const useStyles = makeStyles(theme => ({
    root: {
        width: '70%',
        margin: '50px auto',
        padding: '20px 40px',
    },
    weather: {
        textTransform: 'capitalize'
    },
}));


function WeatherModule(props) {
    const classes = useStyles();
    const image =  `https://openweathermap.org/img/w/${props.image}.png`
    var date = FormatDate()


    return (
        <div>
            <Paper className={classes.root}>
                <div className="row">
                    <div className="col-12">
                        <Typography variant="h6">
                            {props.city}
                        </Typography>
                        <Moment date={date} interval={30000} format="DD/MM/YYYY" />
                        <Typography variant="h6" className={classes.weather}>
                            {props.weather}
                        </Typography>
                    </div>
                </div>
                <div className="row">
                    <div className="col-12">
                        <Tilt>
                            <img src={image} alt={props.weather} />
                        </Tilt>
                    </div>
                </div>
                <div className="row">
                    <div className="col-12">
                        <Typography variant="h6">
                            Max Temp: {props.temp_max}
                        </Typography>
                        <Typography>
                            Min Temp: {props.temp_min}
                        </Typography>
                    </div>
                </div>
            </Paper>
        </div>
    );
}

export default WeatherModule;
