import React, { useState } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { List, ListItem, ListItemIcon, ListItemText, Divider, Icon, Typography} from '@material-ui/core';
import EditEmail  from '../../components/Social/EditSettings/EditEmail';
import EditPassword from '../../components/Social/EditSettings/EditPasword';
import EditAvatar from '../../components/Social/EditSettings/EditAvatar';


const useStyles = makeStyles(theme => ({
    root: {
        width: '100%',
        maxWidth: 360,
        backgroundColor: theme.palette.background.paper,
    },
}));

function Settings() {

    const classes = useStyles();

    const [email, setEmail] = React.useState(false);
    const [password, setPassword] = React.useState(false);
    const [avatar, setAvatar] = React.useState(false);

    function handleClick(item) {
        if(item === 'email'){return setEmail(!email)}
        if(item === 'password'){return setPassword(!password)}
        if(item === 'avater'){return setAvatar(!avatar)}
    }

    function renderComponent() {
        if(email){return <EditEmail />}
        if(password){return <EditPassword />}
        if(avatar){return <EditAvatar />}
    }

    return (
      <div className="container-fluid">
          <div className="row">
            <div className="col-3">
                <Typography>
                    Account Settings
                </Typography>
                <Divider/>
                <List component="nav">
                    <ListItem button onClick={() => handleClick('email')}>
                        <ListItemIcon>
                            <Icon>alternate_email</Icon>
                        </ListItemIcon>
                        <ListItemText primary="Edit Email" />
                    </ListItem>
                    <Divider/>
                    <ListItem buttononClick={() => handleClick('password')}>
                        <ListItemIcon>
                            <Icon>security</Icon>
                        </ListItemIcon>
                        <ListItemText primary="Edit Password" />
                    </ListItem>
                    <Divider/>
                    <ListItem button onClick={() => handleClick('avatar')}>
                        <ListItemIcon>
                            <Icon>supervised_user_circle</Icon>
                        </ListItemIcon>
                        <ListItemText primary="Change Avatar" />
                    </ListItem>
                </List>
            </div>
            <div className="col-9">
                {renderComponent()}
            </div>
          </div>

      </div>  
    );
}

export default Settings;
