import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {
    Grid,
    Typography,
    Button,
} from '@material-ui/core';

import BG from './imgs/reset.jpg';
import TopRight from './imgs/TopRight.png';
import Logo from './imgs/Logo.png';

const useStyles = makeStyles((theme) => ({
    container: {
        paddingTop: 60,
        color: 'white',
        backgroundImage: `url(${BG})`,
        backgroundSize: 'cover',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'flex-start',
        flexWrap: 'wrap',
    },
    introContainer: {
        padding: 20,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexWrap: 'wrap',
    },
    logo: {
        width: '70%',
        height: 'auto',
    },
    header: {
        margin: '20px 0px',
    },
    button: {
        marginTop: 40,
        padding: 15,
        color: '#0000a2',
        background: 'white'
    },
    topDiv: {
        height: 160,
        width: '100%',
        backgroundImage: `url(${TopRight})`,
        backgroundSize: 'cover',
    },
}));

const Intro = () => {

    const classes = useStyles();

    return (
        <Grid
            className={classes.container}
            item
            xs={12}
        >
            <div className={classes.introContainer}>

                <img
                    className={classes.logo}
                    src={Logo}
                    alt="Heath Banner Logo"
                />

                <Typography
                    className={classes.header}
                    variant="h5"
                    align="center"
                >
                    Develop successful web, mobile applications 
                    in weeks, not months
                </Typography>

                <Typography
                    className={classes.body}
                    variant="body1"
                    align="center"
                >
                    using my full-stack knowledge to bring together the tech, the talent 
                    and the experience to develop your web and mobile 
                    applications in record time
                </Typography>

                <Button className={classes.button}>
                    <Typography variant="body2">
                        Discuss your project with me
                    </Typography>
                </Button>
            </div>

            <div className={classes.topDiv}></div>

        </Grid>
    );
};

export default Intro;
