import React from 'react';
import io from 'socket.io-client';

export  const CTX = React.createContext();

const initState = {
    chats: {},
}

function reducer(state=initState, action) {
    const { from, msg, to } = action.payload.msg;

    console.log(from, msg, to);
    switch(action.type) {
        case 'RECIEVE_MESSAGE':
            newMessage[to] = [...state[to], {from, msg}]
            return {
                ...state,
                chats: {
                    [to]: [...state.chats[to], {from, msg}]
                }
            }
        case 'NEW_ROOM':
            const nextState = {...state};
            nextState[to] = [];
            return {
                nextState
            }
        default:
            return state
    }
}
let socket;


function Store(props) {

    const [chats, dispatch] = React.useReducer(reducer, initState);
        function sendChatAction(value) {
            socket.emit('chat message', value);
        }

    if(props.username) {
        socket.emit('new user', props.username)
    }

    if (!socket) {
        socket = io(':3001');

        socket.on('chat message', function(res) {
            console.log(res)
            dispatch({type: 'RECIEVE_MESSAGE', payload: res});
        })
    }

    const user = props.username ? props.username.username : 'Anon';

    return (
        <CTX.Provider value={{chats, sendChatAction, user}}>
            {props.children}
        </CTX.Provider>
    )
}

export default Store
