import React, { Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Typography, GridList, GridListTile, GridListTileBar, ListSubheader, IconButton, Icon } from '@material-ui/core';

import Background from './imgs/1x/skills.png';

import ReactLogo from './imgs/React.png';
import Material from './imgs/MaterialUI.png';
import Mongo from './imgs/mongodb.png';
import MySQL from './imgs/mysql.svg';
import HandleBars from './imgs/handlebars.png';
import Passport from './imgs/passport.png';


const tileData = [
    {
        img: ReactLogo,
        title: 'React JS',
    },
    {
        img: Material,
        title: 'Material UI',
    },
    {
        img: Mongo,
        title: 'Mongo DB'   
    },
    {
        img: MySQL,
        title: 'MySQL'
    },
    {
        img: HandleBars,
        title: 'Handle Bars'
    },
    {
        img: Passport,
        title: 'Passport'
    }
]

const useStyles = makeStyles(theme => ({
    container: {
        backgroundImage: `url(${Background})`,
        backgroundSize: 'cover',
        height: '50vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    header: {
        marginTop: 40,
    },
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        // overflow: 'hidden',
        overflowY: 'none',
        backgroundColor: theme.palette.background.paper,
    },
    gridList: {
        width: 500,
        height: 450,
    },
    tile: {
        overflowY: 'visible',
    },
    icon: {
        color: 'rgba(255, 255, 255, 0.54)'
    },
}));

const Skills = () => {

    const classes = useStyles();

    return (

        <Fragment>

            <Grid className={classes.container} item xs={12}>

                <Typography className={classes.header} variant="h1">
                    What Do I Know?
                </Typography>

            </Grid>
            <Grid item xs={12}>

                <div className={classes.root}>

                    <GridList cellHeight={180} className={classes.gridList}>

                        <GridListTile key="Subheader" cols={2} style={{ height: 'auto' }}>

                        <ListSubheader component="div">December</ListSubheader>

                        </GridListTile>
                        {
                            tileData.map(tile => (

                                <GridListTile className={classes.tile} key={tile.img}>

                                    <img src={tile.img} alt={tile.title} />

                                    <GridListTileBar
                                        title={tile.title}
                                        actionIcon={
                                            <IconButton aria-label={`info about ${tile.title}`} className={classes.icon}>
                                                <Icon>info</Icon>
                                            </IconButton>
                                        }
                                    />

                                </GridListTile>
                            ))
                        }

                    </GridList>

                </div>

            </Grid>

        </Fragment>
    );
};

export default Skills;
