import React, { Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Paper, Avatar, Typography, Divider } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
    headerContainer: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    listContainer: {
        height: '100vh',
        perspective: 7000,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
    },
    icons: {
        [theme.breakpoints.down('xs')]: {
            height: 40,
            width: 40,  
            left: 10,
        },
        height: 120,
        width: 120,
        position: 'absolute',
        top: 20,
        left: 20,
        background: 'linear-gradient(90deg, #FFF 0%,transparent 100%),linear-gradient(90deg, #FFF 0%,transparent 100%),linear-gradient(135deg, rgba(111, 111, 111, 0.01) 0%, rgba(111, 111, 111, 0.01) 24%,rgba(104, 104, 104, 0.01) 24%, rgba(104, 104, 104, 0.01) 100%),linear-gradient(45deg, rgba(143, 143, 143, 0.03) 0%, rgba(143, 143, 143, 0.03) 78%,rgba(8, 8, 8, 0.03) 78%, rgba(8, 8, 8, 0.03) 100%),linear-gradient(0deg, rgba(0, 0, 0, 0.04) 0%, rgba(0, 0, 0, 0.04) 73%,rgba(254, 254, 254, 0.04) 73%, rgba(254, 254, 254, 0.04) 100%),linear-gradient(135deg, rgba(165, 165, 165, 0.06) 0%, rgba(165, 165, 165, 0.06) 64%,rgba(85, 85, 85, 0.06) 64%, rgba(85, 85, 85, 0.06) 100%),linear-gradient(0deg, rgba(219, 219, 219, 0.08) 0%, rgba(219, 219, 219, 0.08) 55%,rgba(0, 0, 0, 0.08) 55%, rgba(0, 0, 0, 0.08) 100%),linear-gradient(135deg, rgba(234, 234, 234, 0.03) 0%, rgba(234, 234, 234, 0.03) 23%,rgba(40, 40, 40, 0.03) 23%, rgba(40, 40, 40, 0.03) 100%),linear-gradient(135deg, rgba(197, 197, 197, 0.06) 0%, rgba(197, 197, 197, 0.06) 70%,rgba(168, 168, 168, 0.06) 70%, rgba(168, 168, 168, 0.06) 100%),linear-gradient(45deg, rgba(205, 205, 205, 0.07) 0%, rgba(205, 205, 205, 0.07) 72%,rgba(183, 183, 183, 0.07) 72%, rgba(183, 183, 183, 0.07) 100%),linear-gradient(135deg, rgba(124, 124, 124, 0.06) 0%, rgba(124, 124, 124, 0.06) 85%,rgba(71, 71, 71, 0.06) 85%, rgba(71, 71, 71, 0.06) 100%),linear-gradient(90deg, rgb(161, 161, 161),rgb(167, 167, 167))',
    },
    infoHolder: {
        position: 'relative',
        height: 100,
        width: '50%',
    },
    li: {
        [theme.breakpoints.down('lg')]: {
            justifyContent: 'center',
            padding: '50px 20px 50px 100px',
        },
        [theme.breakpoints.down('md')]: {
            width: 350,
            flexWrap: 'wrap',
            margin: 0,
        },
        [theme.breakpoints.down('xs')]: {
            width: '70%',
            padding: '10px 10px 10px 50px'
        },
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
        margin: '0 auto',
        cursor: 'pointer',
        transform: 'rotateY(-30deg) rotateX(15deg)',
        padding: 50,
        width: '80%',
        color: 'white',
        transition: 'all .4s ease-out',
        background: 'linear-gradient(135deg, rgba(47, 47, 47, 0.08) 0%, rgba(47, 47, 47, 0.08) 90%,rgba(75, 75, 75, 0.08) 90%, rgba(75, 75, 75, 0.08) 100%),linear-gradient(45deg, rgba(172, 172, 172, 0.1) 0%, rgba(172, 172, 172, 0.1) 65%,rgba(18, 18, 18, 0.1) 65%, rgba(18, 18, 18, 0.1) 100%),linear-gradient(90deg, rgba(157, 157, 157, 0.04) 0%, rgba(157, 157, 157, 0.04) 83%,rgba(198, 198, 198, 0.04) 83%, rgba(198, 198, 198, 0.04) 100%),linear-gradient(0deg, rgba(189, 189, 189, 0.07) 0%, rgba(189, 189, 189, 0.07) 22%,rgba(127, 127, 127, 0.07) 22%, rgba(127, 127, 127, 0.07) 100%),linear-gradient(0deg, rgba(79, 79, 79, 0.03) 0%, rgba(79, 79, 79, 0.03) 34%,rgba(96, 96, 96, 0.03) 34%, rgba(96, 96, 96, 0.03) 100%),linear-gradient(135deg, rgba(54, 54, 54, 0.05) 0%, rgba(54, 54, 54, 0.05) 68%,rgba(192, 192, 192, 0.05) 68%, rgba(192, 192, 192, 0.05) 100%),linear-gradient(0deg, rgba(62, 62, 62, 0.08) 0%, rgba(62, 62, 62, 0.08) 99%,rgba(114, 114, 114, 0.08) 99%, rgba(114, 114, 114, 0.08) 100%),linear-gradient(0deg, rgba(227, 227, 227, 0.07) 0%, rgba(227, 227, 227, 0.07) 83%,rgba(129, 129, 129, 0.07) 83%, rgba(129, 129, 129, 0.07) 100%),linear-gradient(45deg, rgba(27, 27, 27, 0.1) 0%, rgba(27, 27, 27, 0.1) 27%,rgba(58, 58, 58, 0.1) 27%, rgba(58, 58, 58, 0.1) 100%),linear-gradient(90deg, rgb(107, 107, 107),rgb(2, 2, 2))',
        '&:nth-child(1)': {
            [theme.breakpoints.down('md')]: {
                '&:hover': {
                    transform: 'rotateY(5deg) rotateX(7deg) scale(1.03)',
                    backgroundPosition: '0px 25px'
                },
                transform: 'rotateY(-25deg)',
                marginBottom: 15,
            },
        },
        '&:nth-child(2)': {
            [theme.breakpoints.down('md')]: {
                '&:hover': {
                    transform: 'rotateY(-20deg) rotateX(7deg) scale(1.03)',
                    backgroundPosition: '0px 25px'
                },
                transform: 'rotateY(22deg)',
                marginBottom: 10,
            },
        },
        '&:nth-child(3)': {
            [theme.breakpoints.down('md')]: {
                '&:hover': {
                    transform: 'rotateY(5deg) rotateX(7deg) scale(1.03)',
                    backgroundPosition: '0px 25px'
                },
                transform: 'rotateY(-25deg)',
            },
        },
        '&:hover': {
            transform: 'rotateY(-22deg) rotateX(7deg) scale(1.03)',
            backgroundPosition: '0px 25px'
        },
    },
    description: {
        marginTop: 20,
        color: 'lightgray'
    },
    divider: {
        width: '80%',
        backgroundColor: 'white'
    },
    actionIcons: {
        marginLeft: 20,
    },
}));

const PreviousWork = () => {

    const classes = useStyles();

    return (
        <Fragment>

            <Grid className={classes.headerContainer} item xs={6}>

                <Typography variant="h1" align="center">
                    Previous Work
                </Typography>

            </Grid>
            <Grid className={classes.listContainer} item xs={6}>

                <Paper className={classes.li}>

                    <Avatar className={classes.icons}>
                        <img style={{ height: 100, width: 100 }} src="/imgs/logo.png" alt="Future Scaper Logo" />
                    </Avatar>
                    <Typography variant="h4" align="center">
                        Future Scaper
                    </Typography>

                    <Divider className={classes.divider} />

                    <Typography className={classes.description} variant="h6" align="center">
                        This app uses the USDA database to help our users form better and more 
                        precise landscaping projects.
                    </Typography>

                </Paper>
                <Paper className={classes.li}>

                    <Avatar className={classes.icons}>
                        <img style={{ height: 100, width: 100 }} src="/imgs/logo.png" alt="Mountain Dream Logo" />
                    </Avatar>
                    <Typography variant="h4" align="center">
                        Mountain Dream
                    </Typography>

                    <Divider className={classes.divider} />

                    <Typography className={classes.description} variant="h6" align="center">
                        Mountain Dream is an application built to showcase a rental cabin in the mountains 
                        of Boone, NC. The client will be able to maintain their blog by 
                        replicating the basic functions of Google Docs so they can create, edit and 
                        delete blog articles.
                    </Typography>

                </Paper>
                <Paper className={classes.li}>

                    <Avatar className={classes.icons}>
                        <img style={{ height: 100, width: 100 }} src="/imgs/rps.png" alt="RPS Logo" />
                    </Avatar>
                    <Typography variant="h4" align="center">
                        Rock Paper Scissor
                    </Typography>

                    <Divider className={classes.divider} />

                    <Typography className={classes.description} variant="h6" align="center">
                        RPS is a Rock Paper Scissor real-time game. It uses Firestore and Firebase 
                        for messaging, authentication and in-game experience.
                    </Typography>

                </Paper>

            </Grid>

        </Fragment>
    );
};

export default PreviousWork;
