import React, { useEffect, useState, Fragment } from 'react';

import { makeStyles, useTheme } from '@material-ui/core/styles';
import { Divider, useMediaQuery, Grid, GridList, GridListTile, Typography, Paper } from '@material-ui/core';

import Search from './Search';
import WeatherModule from './WeatherModule';
import Description from './Description';

import weatherBG from './imgs/1x/weatherBGNew.png'

import 'typeface-roboto';

const useStyles = makeStyles(theme => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
      width: '100%',
      marginTop: 180
    },
    gridList: {
      flexWrap: 'nowrap',
      transform: 'translateZ(0)',
    },
    weatherSection: {
        [theme.breakpoints.up('md')]: {
            height: '160vh',
        },
        [theme.breakpoints.up('sm')]: {
            height: '170vh',
        },
        [theme.breakpoints.down('sm')]: {
            height: '185vh',
        },
        [theme.breakpoints.down('xs')]: {
            height: '180vh',
        },
        backgroundImage: `url(${weatherBG})`,
        backgroundSize: 'cover',
        height: '160vh',
    },
    concept: {
        marginTop: '30px',
        marginLeft: '80px',
    },
}));

function Weather(props) {

    const [weather, setWeather] = useState('');
    const [isLoaded, setIsLoaded] = useState(false);
    const [sentRequest, setSentRequest] = useState(false);
    const [parsedForecast, setParsedForecast] = useState('');

    const classes = useStyles();
    const theme = useTheme();

    const lg = useMediaQuery(theme.breakpoints.up('md'));
    const md = useMediaQuery(theme.breakpoints.down('md'));
    const sm = useMediaQuery(theme.breakpoints.down('sm'));
    const xs = useMediaQuery(theme.breakpoints.down('xs'));

    const getVariant = () => {
        if(xs) { return 'h4' }
        if(sm) { return 'h3' }
        else { return 'h2' }
    };

    useEffect(() => {
        fetch('https://api.openweathermap.org/data/2.5/forecast?lat=35.227085&lon=-80.843124&units=imperial&APPID=4216d1350fe31af9bf5100bb34fa72e2')
            .then(res => res.json())
            .then((result) => { 
                setWeather(result);
            });
    }, []);

    useEffect(() => {
        if (!weather) { return }
        let dt = '';
        const filteredResult = weather.list.filter((object) => {
            if(dt === '') {
                dt = object.dt_txt.split(' ');
                dt = dt[0];
                return true;
            }
            const sample = object.dt_txt.split(' ')
            if (dt !== sample[0]) {
                dt = sample[0];
                return true;
            }
        })
        setParsedForecast(filteredResult);
        props.grabWeather(filteredResult[0]);
    }, [weather]);

    function updateModule(coords) {
        setParsedForecast('');
        fetch(`https://api.openweathermap.org/data/2.5/forecast?lat=${coords[0].coord.lat}&lon=${coords[0].coord.lon}&units=imperial&APPID=4216d1350fe31af9bf5100bb34fa72e2`)
            .then(res => res.json())
            .then((result) => {
                setWeather(result); 
            });
    };

    return (
            <Grid container className={classes.weatherSection}>

                    <Grid item xs={12}>

                        <Typography className={classes.concept} align="center" color="primary" variant={getVariant()}>
                            Weather Concept
                        </Typography>

                    </Grid>

                    {
                        parsedForecast ?

                        <WeatherModule 
                            forecast={parsedForecast}
                            city={weather.city.name}
                            country={weather.city.country}
                            // date={object.dt_txt}
                        />
                            : 
                        ''
                    }

                    <Grid container>

                        <Grid item xs={12}>
                                <div >
                                    <Search updateModule={updateModule} />
                                </div>
                        </Grid>

                        <Grid item xs={12}>

                            <Description />

                        </Grid>

                    </Grid>
            </Grid>
    );
}

export default Weather;
