import React, { Fragment, useContext, useState } from 'react';

import { AppContext } from '../../../context/AuthContext';

import { TextField, Icon, Fab } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

import Moment from 'moment';

const useStyles = makeStyles(theme => ({
    root: {
        color: 'black !important',
    },
    postStory: {
        marginLeft: '10px',
        backgroundColor: 'rgb(255, 145, 71)'
    },
    form: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        width: '80%',
        margin: '20px auto',
    },
    textField: {
        width: '80%',
    },
    input: {
        fontSize: '1.5rem',
        padding: '10px 0px',
    },
    underline: {
        '&:before': {
            borderBottom: '1px solid rgb(129, 0, 206) !important',
        },
        '&:after': {
            borderBottom: '2px solid rgb(255, 145, 71) !important',
        },
        '&:hover:before': {
            borderBottom: '2px solid rgb(129, 0, 206) !important',
        },
    },
    label: {
        fontSize: '1.5rem',
        '&.Mui-focused': {
            color: 'rgb(255, 145, 71)'
        },
    },
    helperText: {
        fontSize: '1.2rem',
    },
}));

const Story = () => {

    const classes = useStyles();
    const holder = useContext(AppContext);

    const [story, setStory] = useState('');

    const handleInputChange = (e) => {
        const { value } = e.target;
        setStory(value);
    };

    const handleStoryPost = () => {
        const userId = holder.auth._id;
        const infoId = holder.auth.info._id;
        const time = Moment();
        fetch('/api/social/newStory', {
            method: 'POST',
            body: JSON.stringify({ userId, infoId, story, time }),
            headers: { 'Content-Type': 'application/json' }
        })
            .then(res => res.json())
            .then((user) => {
                setStory('');
                holder.getNewStory();
            });
    };

    return (
        <Fragment>

            <div className={classes.form}>

                <TextField
                    className={classes.textField}
                    FormHelperTextProps={{ className: classes.helperText }}
                    InputProps={{ classes: {
                        underline: classes.underline,
                        input: classes.input,
                        }
                    }}
                    InputLabelProps={{ className: classes.label }}
                    multiline
                    fullWidth={false}
                    rowsMax="8"
                    label="Story"
                    helperText="Story here..."
                    name="setStory"
                    value={story}
                    onChange={handleInputChange}
                />

                <Fab
                    className={classes.postStory}
                    onClick={handleStoryPost}
                    size={holder.sm ? 'small' : 'large'}
                    color="primary"
                >
                    <Icon fontSize={holder.sm ? 'small' : 'large'}>create</Icon>
                </Fab>

            </div>

        </Fragment>
    );
};

export default Story;
