import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {
    Grid,
    Typography,
} from '@material-ui/core';

import Experience from './imgs/why/speed.svg';
import Market from './imgs/why/focus.svg';
import Improvement from './imgs/why/kaizen.svg';

const useStyles = makeStyles((theme) => ({
    container: {
        backgroundColor: '#0000a2',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexWrap: 'wrap',
    },
    why: {
        marginTop: 20,
        color: 'white',
    },
    whyContainers: {
        padding: 20,
        color: 'white',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexWrap: 'wrap',
    },
    banners: {
        width: '50%',
        height: 'auto',
    },
    headers: {
        width: '100%',
        marginBottom: 20,
    },
    body: {
        width: '100%',
    }
}));

const Why = () => {

    const classes = useStyles();

    return (
        <Grid className={classes.container} container>

            <Typography
                className={classes.why}
                variant="h4"
                align="center"
            >
                Why Heath?
            </Typography>

            <Grid
                className={classes.whyContainers}
                xs={12}
                sm={4}
                item
            >

                <img
                    className={classes.banners}
                    src={Experience}
                    alt="Experience Banner"
                />

                <Typography
                    className={classes.headers}
                    variant="h5"
                    align="center"
                >
                    Experience
                </Typography>
                <Typography
                    className={classes.body}
                    align="center"
                >
                    Software development best practices used by most
                    major companies throughout the US
                </Typography>
            </Grid>
            <Grid
                className={classes.whyContainers}
                xs={12}
                sm={4}
                item
            >

                <img
                    className={classes.banners}
                    src={Market}
                    alt="Market Banner"
                />

                <Typography
                    className={classes.headers}
                    variant="h5"
                    align="center"
                >
                    Time To Market
                </Typography>
                <Typography
                    className={classes.body}
                    align="center"
                >
                    Launch new software in weeks, not months
                </Typography>
            </Grid>
            <Grid
                className={classes.whyContainers}
                xs={12}
                sm={4}
                item
            >

                <img
                    className={classes.banners}
                    src={Improvement}
                    alt="Improvement Banner"
                />

                <Typography
                    className={classes.headers}
                    variant="h5"
                    align="center"
                >
                    Continuous Improvement
                </Typography>
                <Typography
                    className={classes.body}
                    align="center"
                >
                    Embracing Lean and Kaizen, the Japanese practices for 
                    continuous improvement, allows us to release quickly without 
                    compromising quality
                </Typography>
            </Grid>

        </Grid>
    );
};

export default Why;
