import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {
    Grid,
    Typography,
    Button,
} from '@material-ui/core';

import Nav from '../../components/Navigation/Nav';

import BG from './imgs/reset.jpg';
import Logo from './imgs/Logo.png';

const useStyles = makeStyles((theme) => ({
    container: {
        backgroundImage: `url(${BG})`,
        backgroundSize: 'cover',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'flex-start',
    },
    introContainer: {
        color: 'white',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexWrap: 'wrap',
    },
    logo: {
        width: '70%',
        height: 'auto',
    },
    header: {
        margin: '20px 0px',
    },
    button: {
        margin: '20px 0px',
        padding: 15,
        color: '#0000a2',
        background: 'white'
    },
}));

const Home = () => {

    const classes = useStyles();

    return (
        <Grid className={classes.container} container>


                <Nav />

            <Grid
                className={classes.introContainer}
                item
                xs={12}
            >

                <img
                    className={classes.logo}
                    src={Logo}
                    alt="Heath Banner Logo"
                />

                <Typography
                    className={classes.header}
                    variant="h5"
                    align="center"
                >
                    Develop successful web, mobile and software applications in weeks, not months
                </Typography>

                <Typography
                    className={classes.body}
                    variant="body1"
                    align="center"
                >
                    Our Charlotte team of full-stack developers and agile experts bring together the tech, the talent and the experience to develop your web, mobile and software applications in record time
                </Typography>

                <Button className={classes.button}>
                    <Typography variant="body2">
                        Discuss your project with us
                    </Typography>
                </Button>

            </Grid>

        </Grid>
    );
};

export default Home;
