import React, { useEffect, useState } from 'react';
import { Parallax } from 'react-parallax';
import WeatherModule from './WeatherModule';
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import Search from './Search';

const useStyles = makeStyles(theme => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
      height: '300px',
      width: '100%',
    },
    gridList: {
      flexWrap: 'nowrap',
      transform: 'translateZ(0)',
    },
    weatherInfo: {
        background: 'rgb(237, 237, 237, 0.7)',
        borderRadius: '5px',
        width: '70%',
        margin: '50px auto',
        padding: '20px 40px',
        fontSize: '17px',
    },
    weatherInfoTest: {
        background: weatherToday.weather[0].main === 'Clouds' ? 'rgb(0, 0, 0)' : 'rgb(237, 237, 237, 0.7)',
        borderRadius: '5px',
        width: '70%',
        margin: '50px auto',
        padding: '20px 40px',
        fontSize: '17px',
    }
  }));


function Weather() {

    const [weather, setWeather] = useState('');
    const [weatherToday, setWeatherToday] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [sentRequest, setSentRequest] = useState(false);
    const [parsedForecast, setParsedForecast] = useState(null);

    const classes = useStyles();


    useEffect(() => {
        if (!sentRequest) {
            setSentRequest(true)
            fetch('http://api.openweathermap.org/data/2.5/forecast?lat=35.227085&lon=-80.843124&APPID=4216d1350fe31af9bf5100bb34fa72e2')
            .then(res => res.json())
            .then((result) => { console.log(result); setWeather(result); setSentRequest(true); setIsLoaded(true) });
        }
    })

    if (isLoaded) {
        var dt = '';
        const filteredResult = weather.list.filter(object => {
            if(dt === '') {dt = object.dt_txt.split(' '); dt = dt[0]; return true}
            const sample = object.dt_txt.split(' ')
            if (dt !== sample[0]) {dt = sample[0]; console.log(sample[0]); return true}
        })
        setParsedForecast(filteredResult);
        setIsLoaded(false);
        setWeatherToday(filteredResult[0]);
        console.log(filteredResult[0]);
    }

    var weatherInfo = [];

    if (parsedForecast) {
        console.log('PARSE')
        weatherInfo = parsedForecast.map(object => {
            return (
            <GridListTile >
                <WeatherModule 
                    weather={object.weather[0].description}
                    image={object.weather[0].icon}
                    temp_max={object.main.temp_max}
                    temp_min={object.main.temp_min}
                    city={weather.city.name}
                    country={weather.city.country}
                    date={object.dt_txt}
                />
            </GridListTile>
            )
        })
    } 

    function updateModule(coords) {
        console.log(coords)
        fetch(`http://api.openweathermap.org/data/2.5/forecast?lat=${coords[0].coord.lat}&lon=${coords[0].coord.lon}&APPID=4216d1350fe31af9bf5100bb34fa72e2`)
        .then(res => res.json())
        .then((result) => { console.log(result); setWeather(result); setIsLoaded(true) });
    }


    return (
        <div>
            <Parallax
                bgImage={require('./imgs/gabriele-diwald.jpg')}
                bgImageAlt="Weather"
                strength={400}
            >
                <div className="weather-section">
                    <div className="row">
                        <div className="col-12">
                            <h1 id="weather-header">Weather Concept</h1>
                        <div className={classes.root}>

                            <GridList cellHeight={'auto'} className={classes.gridList} cols={4}>
                                {weatherInfo}
                            </GridList>
                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-6">
                            <div className={classes.weatherInfoTest}>
                                <h3>Description</h3>
                                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
                                    Fusce sed elit nec nulla finibus vestibulum. Duis posuere 
                                    ligula elit, id ornare turpis scelerisque eu. Curabitur 
                                    id gravida velit, nec condimentum diam. Curabitur mattis, 
                                    nisl vel accumsan porttitor, nisl diam ullamcorper leo, 
                                    vel luctus justo sapien et turpis. Curabitur mattis justo 
                                    massa, volutpat hendrerit nisi consequat id. Nulla auctor, 
                                    sapien a posuere rutrum, lectus ex fermentum enim, et 
                                    eleifend ipsum erat in tellus. Aenean dignissim porta porttitor. 
                                    Curabitur at tempus ante. Aliquam malesuada velit in leo 
                                    mollis, in eleifend odio auctor.
                                </p>
                            </div>
                        </div>
                        <div className="col-6">

                            <Search updateModule={updateModule} />
                        </div>
                    </div>
                </div>
            </Parallax>
        </div>
    );
}

export default Weather;
