import React, { Fragment } from 'react';

import { makeStyles, useTheme } from '@material-ui/core/styles';
import { MobileStepper, Button, Icon, Typography, Paper } from '@material-ui/core';

const useStyles = makeStyles({
    root: {
        maxWidth: 400,
        flexGrow: 1,
    },
    stepperContainer: {
        width: '60%',
        marginTop: 40,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexWrap: 'wrap',
    },
    paper: {
        height: 300,
        padding: 20,
        borderRadius: '0px 0px 4px 4px',
        background: 'linear-gradient(45deg, #ffffff 30%, #ece9e6 90%)'
    },
    button: {
        margin: '10px 20px 0px 0px',
        width: '40%',
    },
});

const Mobile = (props) => {

    const classes = useStyles();
    const theme = useTheme();

    return (
        <div className={classes.stepperContainer}>

            <MobileStepper
                variant="progress"
                steps={props.step}
                position="static"
                activeStep={props.activeStep}
                className={classes.root}
                nextButton={
                    <Button size="small" onClick={props.handleNext} disabled={props.activeStep === props.steps.length - 1}>
                        Next
                        {theme.direction === 'rtl' ? <Icon>keyboard_arrow_left</Icon> : <Icon>keyboard_arrow_right</Icon>}
                    </Button>
                }
                backButton={
                    <Button size="small" onClick={props.handleBack} disabled={props.activeStep === 0}>
                        {theme.direction === 'rtl' ? <Icon>keyboard_arrow_right</Icon> : <Icon>keyboard_arrow_left</Icon>}
                        Back
                    </Button>
                }
            />

            {
                props.activeStep === props.steps.length 
                    ? 
                (
                    <Fragment>

                        <Typography className={classes.instructions}>
                            All steps completed - you&apos;re finished
                        </Typography>

                        <Button 
                            onClick={props.handleReset} 
                        >
                            Reset
                        </Button>

                    </Fragment>
                ) 
                    :   
                (
                    <Fragment>

                        <Paper className={classes.paper}>

                            <Typography className={classes.instructions} variant="body1">
                                {props.getContent(props.activeStep)}
                            </Typography>

                        </Paper>

                        <Button 
                            variant="contained"
                            color="primary" 
                            disabled={props.activeStep === 0} 
                            onClick={props.handleBack} 
                            className={classes.button}
                        >
                            Back
                        </Button>

                        <Button
                            variant="contained"
                            color="primary"
                            onClick={props.activeStep === props.steps.length - 1 ? props.handleReset : props.handleNext}
                            style={{marginTop: 10, width: '40%'}}
                        >
                            {props.activeStep === props.steps.length - 1 ? 'Start Over' : 'Next'}
                        </Button>

                    </Fragment>
                )
            }

        </div>

    );
};

export default Mobile;
