import React, { Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { List, ListItem, ListItemIcon, ListItemText, Divider, Icon, Typography } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
    root: {
        width: '100%',
        maxWidth: 360,
        backgroundColor: theme.palette.background.paper,
    },
    listItem: {
        display: 'block'
    },
    secondary: {
        display: 'block',
        margin: '10px 0px',
    },
}));

const education = {
    education: 'Central Piedmont Community College',
    selfTaught: 'Photoshop, Python',
    skills: 'Web Development (Javascript, JQuery HTML, CSS, React, Material UI, MongoDB, MySQL)'    
};

const contact = {
    email: 'heathbanner@outlook.com',
    phone: '(704)942-6721',
    linkedIn: 'https://www.linkedin.com/in/heath-banner-76b59b187/',
}


function About() {

    const classes = useStyles();

    function renderEducation() {

        return (
            <List>
                <ListItem className={classes.listItem}>
                    <ListItemText primary="Education" />
                    <Divider />
                    <Typography className={classes.secondary} component="p" variant="body2" color="textPrimary">
                        {education.education}
                    </Typography>
                </ListItem>
                <ListItem className={classes.listItem}>
                    <ListItemText primary="Self Taught" />
                    <Divider />
                    <Typography className={classes.secondary} component="p" variant="body2" color="textPrimary">
                        {education.selfTaught}
                    </Typography>
                </ListItem>
                <ListItem className={classes.listItem}>
                    <ListItemText primary="Skills" />
                    <Divider />
                    <Typography className={classes.secondary} component="p" variant="body2" color="textPrimary">
                        {education.skills}
                    </Typography>
                </ListItem>
            </List>
        )
    }

    function renderContact() {

        return (
            <List>
                <ListItem className={classes.listItem}>
                    <ListItemText primary="Email" />
                    <Divider />
                    <Typography className={classes.secondary} component="p" variant="body2" color="textPrimary">
                        {contact.email}
                    </Typography>
                </ListItem>
                <ListItem className={classes.listItem}>
                    <ListItemText primary="Phone" />
                    <Divider />
                    <Typography className={classes.secondary} component="p" variant="body2" color="textPrimary">
                        {contact.phone}
                    </Typography>
                </ListItem>
                <ListItem className={classes.listItem}>
                    <ListItemText primary="LinkedIn" />
                    <Divider />
                    <Typography className={classes.secondary} component="p" variant="body2" color="textPrimary">
                        {contact.linkedIn}
                    </Typography>
                </ListItem>
            </List>
        ); 
    }

    return (
        <Fragment>
            <div className="col-4">
                <List component="nav">
                    <ListItem button>
                        <ListItemIcon>
                            <Icon>contacts</Icon>
                        </ListItemIcon>
                        <ListItemText primary="Contact Info" />
                    </ListItem>
                    <ListItem button>
                        <ListItemIcon>
                            <Icon>school</Icon>
                        </ListItemIcon>
                        <ListItemText primary="Work and Education" />
                    </ListItem> 
                </List>
            </div>
            <div className="col-8">
                    {renderContact()}
            </div>
        </Fragment>
    );
}

export default About;
