import React, { Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { List, ListItem, ListItemIcon, ListItemText, Divider, Icon, Typography } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
    root: {
        width: '100%',
        maxWidth: 360,
        backgroundColor: theme.palette.background.paper,
    },
    listItem: {
        display: 'block'
    },
    secondary: {
        display: 'block',
        margin: '10px 0px',
    },
}));

const education = [
    {
    education: 'Central Piedmont Community College',
    },
    {
        selfTaught: 'Photoshop, Python',
    },
    {
        skills: 'Web Development (Javascript, JQuery HTML, CSS, React, Material UI, MongoDB, MySQL)'    
    }
]

function About() {

    const classes = useStyles();

    function renderEducation() {

        return (
            education.map((item, index) => {
                return (
                <ListItem className={classes.listItem}>
                    <ListItemText primary="Education" />
                    <Divider />
                    <Typography className={classes.secondary} component="p" variant="body2" color="textPrimary">
                        {item}
                    </Typography>
                </ListItem>
                );
            })
        )
    }

    return (
        <Fragment>
            <div className="col-4">
                <List component="nav">
                    <ListItem button>
                        <ListItemIcon>
                            <Icon>contacts</Icon>
                        </ListItemIcon>
                        <ListItemText primary="Contact Info" />
                    </ListItem>
                    <ListItem button>
                        <ListItemIcon>
                            <Icon>school</Icon>
                        </ListItemIcon>
                        <ListItemText primary="Work and Education" />
                    </ListItem> 
                </List>
            </div>
            <div className="col-8">
                <List>
                    {renderEducation()}
                </List>
            </div>
        </Fragment>
    );
}

export default About;
