import React, { useEffect } from 'react';
import { Switch, Route, Redirect } from 'react-router-dom';

import Home from './pages/Home/home';
import Social from './pages/Social/Social';
import Login from './pages/Login/Login';
import Register from './pages/Register/Register';
import AuthContext from './context/AuthContext';
import Settings from './pages/Social/Settings';
import FindFriends from './pages/Social/FindFriends';
import Profile from './pages/Social/Profile';

import TokenStore from './lib/TokenStore';

function App() {

  const [auth, setAuth] = React.useState({
    user: undefined,
    authToken: TokenStore.getToken(),
    onLogin: handleLogin,
    onLogout: handleLogout,
    updateAuth: updateAuth,
    loaded: false,
    setLoaded: updateLoaded
  })
  const [validated, setValidated] = React.useState(true);
  const [didMount, setDidMount] = React.useState(false)

  function handleLogin(user, authToken) {
    TokenStore.setToken(authToken);
    setAuth({...auth, user, authToken});
    setValidated(true);
  }

  function handleLogout() {
    TokenStore.clearToken();
    const user = undefined;
    const authToken = undefined;
    setAuth({...auth, user, authToken});
    return <Redirect to="/login" />;
  }

  function updateAuth() {
    console.log('HIT')
    setDidMount(false);
  };

  function updateLoaded(bool) {
    console.log(auth)
    const loaded = bool
    setAuth({...auth, loaded});
  }

  useEffect(() => {
    const { authToken } = auth;
    if(!authToken){return setValidated(false)};
    if(!didMount) {
      setDidMount(true)
      fetch('/api/users/me', {headers:{'Authorization': `Bearer ${authToken}`}})
      .then(res => res.json())
      .then((user) => {
        console.log(user);
        if(!user._id){return setValidated(false)}
        const loaded = true
        setAuth({...auth, user, loaded})
        setValidated(true)
      })
      .catch(err => console.log(err));
    }
  });

  return (
    <AuthContext.Provider value={auth}>
      {!validated ? <Redirect to="/login" /> : ''}
      <div className="App">
        <Switch>
            <Route exact path="/" component={Home} />
            <Route path="/home" component={Home} />
            <Route path="/social" exact component={Social} />
            <Route path="/social/:handle" component={Profile} />
            <Route path="/login" component={Login} />
            <Route path="/register" component={Register} />
            <Route path="/settings" component={Settings} />
            <Route path="/findFriends" component={FindFriends} />
        </Switch>
      </div>
    </AuthContext.Provider>
  );
}

export default App;
