import React, { useState, useContext } from 'react';

import { AppContext } from '../../../context/AuthContext';

import { makeStyles } from '@material-ui/core/styles';
import { Paper, Tabs, Tab, Typography } from '@material-ui/core';

const useStyles = makeStyles({
    root: {
        flexGrow: 1,
        height: '100%',
        overflow: 'hidden',
        zIndex: 1500,
    },
    label: {
        fontSize: '0.9rem'
    }
});

const tabIndex = ['Timeline', 'About', 'Friends', 'Photos'];

const ProfileNav = (props) => {

    const classes = useStyles();
    const holder = useContext(AppContext);

    const [value, setValue] = useState(0);

    const handleChange = (e, newValue) => {
        setValue(newValue);
        const newTab = tabIndex[newValue];
        props.tabChange(newTab);
    };

    return (
        <Paper className={classes.root}>

            <Tabs
                value={value}
                onChange={handleChange}
                indicatorColor="primary"
                textColor="primary"
                centered
                variant="fullWidth"
            >
                <Tab wrapped={true} className={classes.label} label={<Typography variant={holder.sm ? 'body2' : 'h6'}>Timeline</Typography>} />
                <Tab wrapped={true} className={classes.label} label={<Typography variant={holder.sm ? 'body2' : 'h6'}>About</Typography>} />
                <Tab wrapped={true} className={classes.label} label={<Typography variant={holder.sm ? 'body2' : 'h6'}>Friends</Typography>} />
                <Tab wrapped={true} className={classes.label} label={<Typography variant={holder.sm ? 'body2' : 'h6'}>Photos</Typography>} />
            </Tabs>

        </Paper>
    );
};

export default ProfileNav;
