import React, { useEffect } from 'react';
import clsx from 'clsx';
import Select from 'react-select';
import { emphasize, makeStyles, useTheme } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import NoSsr from '@material-ui/core/NoSsr';
import TextField from '@material-ui/core/TextField';
import Paper from '@material-ui/core/Paper';
import Chip from '@material-ui/core/Chip';
import MenuItem from '@material-ui/core/MenuItem';
import Icon from '@material-ui/core/Icon';
import PropTypes from 'prop-types';
import jssPluginPropsSort from 'jss-plugin-props-sort';


const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
    height: 250,
    width: '70%',
    margin: '20px auto',
    color: 'white'
  },
  label: {
    color: 'white',
  },
  input: {
    display: 'flex',
    padding: 0,
    height: 'auto',
    fontColor: 'white !important'
  },
  valueContainer: {
    display: 'flex',
    flexWrap: 'wrap',
    flex: 1,
    alignItems: 'center',
    overflow: 'hidden',
    borderBottom: '1px solid white',
    color: 'white !important'
  },
  chip: {
    margin: theme.spacing(0.5, 0.25),
    color: 'white'
  },
  chipFocused: {
    backgroundColor: emphasize(
      theme.palette.type === 'light' ? theme.palette.grey[900] : theme.palette.grey[100],
      0.08,
    ),
  },
  noOptionsMessage: {
    padding: theme.spacing(1, 2),
  },
  singleValue: {
    fontSize: 16,
    color: 'white'
  },
  placeholder: {
    position: 'absolute',
    left: 2,
    bottom: 6,
    fontSize: 16,
    color: 'white'
  },
  paper: {
    position: 'absolute',
    zIndex: 1,
    marginTop: theme.spacing(1),
    left: 0,
    right: 0,
  },
  divider: {
    height: theme.spacing(2),
  },
}));

function NoOptionsMessage(props) {
  return (
    <Typography
      color="textSecondary"
      className={props.selectProps.classes.noOptionsMessage}
      {...props.innerProps}
    >
      {props.children}
    </Typography>
  );
}

NoOptionsMessage.propTypes = {
  children: PropTypes.node,
  innerProps: PropTypes.object,
  selectProps: PropTypes.object.isRequired,
};

function inputComponent({ inputRef, ...props }) {
  return <div ref={inputRef} {...props} />;
}

inputComponent.propTypes = {
  inputRef: PropTypes.oneOfType([PropTypes.func, PropTypes.object]),
};

function Control(props) {
  const {
    children,
    innerProps,
    innerRef,
    selectProps: { classes, TextFieldProps },
  } = props;

  return (
    <TextField
      fullWidth
      style={{
        color: 'white',
      }}
      name= 'Heath'
      InputProps={{
        inputComponent,
        name: 'Heath',
        value: props.selectProps.value,
        inputProps: {
          onChange: props.selectProps.onChange,
          name: props.selectProps.name,
          value: props.selectProps.value,

          className: classes.input,
          ref: innerRef,
          children,
          ...innerProps,
        },
      }}
      {...TextFieldProps}
    />
  );
}

Control.propTypes = {
  children: PropTypes.node,
  innerProps: PropTypes.object,
  innerRef: PropTypes.oneOfType([PropTypes.func, PropTypes.object]),
  selectProps: PropTypes.object.isRequired,
};

function Option(props) {
  return (
    <MenuItem
      ref={props.innerRef}
      selected={props.isFocused}
      component="div"
      style={{
        fontWeight: props.isSelected ? 500 : 400,
      }}
      {...props.innerProps}
    >
      {props.children}
    </MenuItem>
  );
}

Option.propTypes = {
  children: PropTypes.node,
  innerProps: PropTypes.object,
  innerRef: PropTypes.oneOfType([PropTypes.func, PropTypes.object]),
  isFocused: PropTypes.bool,
  isSelected: PropTypes.bool,
};

function Placeholder(props) {
  return (
    <Typography
      color="textSecondary"
      className={props.selectProps.classes.placeholder}
      {...props.innerProps}
    >
      {props.children}
    </Typography>
  );
}

Placeholder.propTypes = {
  children: PropTypes.node,
  innerProps: PropTypes.object,
  selectProps: PropTypes.object.isRequired,
};

function SingleValue(props) {
  return (
    <Typography className={props.selectProps.classes.singleValue} {...props.innerProps}>
      {props.children}
    </Typography>
  );
}

SingleValue.propTypes = {
  children: PropTypes.node,
  innerProps: PropTypes.object,
  selectProps: PropTypes.object.isRequired,
};


function ValueContainer(props) {


  return <div className={props.selectProps.classes.valueContainer} >{props.children}</div>;
}

ValueContainer.propTypes = {
  children: PropTypes.node,
  selectProps: PropTypes.object.isRequired,
};


function Menu(props) {
  return (
    <Paper square className={props.selectProps.classes.paper} {...props.innerProps}>
      {props.children}
    </Paper>
  );
}

Menu.propTypes = {
  children: PropTypes.node,
  innerProps: PropTypes.object,
  selectProps: PropTypes.object,
};

const components = {
  Control,
  Menu,
  NoOptionsMessage,
  Option,
  Placeholder,
  // SingleValue,
  ValueContainer,
};

var suggestions = [];

const states = [
  'Alabama',
  'Alaska',
  'Arizona',
  'Arkansas',
  'California',
  'Colorado',
  'Connecticut',
  'Delaware',
  'Florida',
  'Georgia',
  'Hawaii',
  'Idaho',
  'Illinois',
  'Indiana',
  'Iowa',
  'Kansas',
  'Louisiana',
  'Maine',
  'Maryland',
  'Massachusetts',
  'Michigan',
  'Minnesota',
  'Mississippi',
  'Missouri',
  'Montana',
  'Nebraska',
  'Nevada',
  'New Hampshire',
  'New Jersey',
  'New Mexico',
  'New York',
  'North Carolina',
  'North Dakota',
  'Ohio',
  'Oklahoma',
  'Oregon',
  'Pennsylvania',
  'Rhode Island',
  'South Carolina',
  'South Dakota',
  'Tennesee',
  'Texas',
  'Utah',
  'Vermont',
  'Virginia',
  'Washington',
  'West Virginia',
  'Wisconsin',
  'Wyoming'
];

export default function IntegrationReactSelect() {
  const classes = useStyles();
  const theme = useTheme();

  const [selectedState, setSelectedState] = React.useState(null);
  const [cities, setCities] = React.useState(null);
  const [isLoaded, setIsLoaded] = React.useState(false);
  const [sentRequest, setSentRequest] = React.useState(false);

  useEffect(() => {
    if(!sentRequest){
      setSentRequest(true);
      fetch('/api/cities/onLoad')
      .then(res => res.json())
      .then((result) => {console.log(result); setCities(result); setIsLoaded(true);})
    }
    if(isLoaded) {
      console.log('isloaded')
      setIsLoaded(false);
      suggestions = cities.map(suggestion => ({
        value: suggestion.name,
        label: suggestion.name,
    }))
    }
  })

  function handleInputChange(event) {
    const { value, name } = event.target
    console.log(name, value);
    if(name === 'setSelectedState'){console.log('STATE'); setSelectedState(value)}
    if(name === 'setCities'){
      fetch('/api/cities/search', {
        method: 'POST',
        body: JSON.stringify({data: value}),
        headers: {'Content-Type': 'application/json'}
      })
      .then(res => res.json())
      .then((result) => {console.log(result); setIsLoaded(true); setCities(result)})
    }
  }



  function populateSuggestions() {
    return 
  }


  const selectStyles = {
    input: base => ({
      ...base,
      color: 'white',
      '& input': {
        font: 'inherit',
      },
    }),
  };

  return (
    <div className={classes.root}>
      <NoSsr>
        <Select
          classes={classes}
          styles={selectStyles}
          inputId="react-select-single"
          TextFieldProps={{
            label: 'City',
            InputLabelProps: {
              htmlFor: 'react-select-single',
              shrink: true,
            },
            placeholder: 'Search for a city',
          }}
          options={cities ? cities.map(suggestion => ({
              value: suggestion.name,
              label: suggestion.name,
        })) : console.log('not yet')}
          components={components}
          name="setCities"
          value={cities}
          onChange={handleInputChange}
        />
        <div className={classes.divider} />
        <Select
          classes={classes}
          styles={selectStyles}
          inputId="react-select-single"
          TextFieldProps={{
            label: 'State',
            InputLabelProps: {
              htmlFor: 'react-select-single',
              shrink: true,
            },
            placeholder: 'Search for a city',
          }}
          options={states.map(suggestion => ({
            value: suggestion,
            label: suggestion,
          }))}
          components={components}
          name="setSelectedState"
          value={selectedState}  
          onChange={handleInputChange}
        />
      </NoSsr>
    </div>
  );
}
