import React, { Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Popper, Button, Typography, Fade, Paper } from '@material-ui/core';

const linkData = {
    futureScaper: {
        repo: `https://github.com/HeathBanner/FutureScaper`,
        website: `https://futurescaper.herokuapp.com/`,
    },
    mtnDream: {
        repo: `https://github.com/HeathBanner/MtnDream`,
        website: `https://mtndream.herokuapp.com/`,
    },
    rps: {
        repo: `https://github.com/HeathBanner/RPS`,
        website: `https://heathbanner.github.io/RPS/`,
    },
};

const useStyles = makeStyles(theme => ({
    paper: {
        padding: 30,
    },
}));

const DescPopper = (props) => {

    const classes = useStyles();

    return (
        <Fragment>

            <Popper
                id={props.id}
                open={props.open}
                anchorEl={props.anchorEl}
                placement="left"
                transition
            >
                {({ TransitionProps }) => (

                    <Fade {...TransitionProps} timeout={350}>

                        <Paper className={classes.paper}>

                            <a
                                href={linkData[props.link].website}
                                target="_blank"
                                rel="noopener noreferrer"
                                style={{ textDecoration: 'none' }}
                            >
                                <Button>
                                    <Typography variant="h5" align="center" className={classes.description}>
                                        Website
                                    </Typography>
                                </Button>
                            </a>

                            <a
                                href={linkData[props.link].repo}
                                target="_blank"
                                rel="noopener noreferrer"
                            >
                                <Button>
                                    <Typography variant="h5" align="center" className={classes.description}>
                                        Repo
                                    </Typography>
                                </Button>
                            </a>

                        </Paper>


                    </Fade>

                )}

            </Popper>

        </Fragment>

    );
};

export default DescPopper;
