import React, { useContext, Fragment } from 'react';
import AuthContext from '../../../context/AuthContext';
import Moment from 'react-moment';

import { makeStyles } from '@material-ui/core/styles';
import { List, ListItem, ListItemText, ListItemAvatar, Divider, Typography, Avatar, Icon, Fab} from '@material-ui/core';

const useStyles = makeStyles(theme => ({
    root: {
        backgroundColor: theme.palette.background.paper,
    },
}));

function Timeline() {

    const classes = useStyles();
    const auth = useContext(AuthContext);
    console.log(auth);

    return (
        <div>
            <List className={classes.root}>
                {auth.user ? auth.user.Stories.map((story, index) => (
                    <Fragment>
                        <ListItem>
                            <ListItemAvatar><Avatar alt={index} src="/imgs/avatar.jpg" /></ListItemAvatar>
                            <ListItemText 
                                primary={story.story} 
                                secondary={
                                    <Moment date={story.time} format={'dddd h:mm a'} />
                                }
                            >
                            </ListItemText>
                            <Fab size="small">
                                <Icon>delete_outline</Icon>
                            </Fab>
                        </ListItem>
                        <Divider />
                    </Fragment>
                )) : ''}
            </List>
        </div>
    );
}

export default Timeline;
