import React, { useContext, Fragment, useState } from 'react';
import AuthContext from '../../../context/AuthContext';
import Moment from 'react-moment';

import { makeStyles } from '@material-ui/core/styles';
import { TextField, Collapse, List, ListItem, ListItemText, ListItemAvatar, Divider, Typography, Avatar, Icon, IconButton} from '@material-ui/core';

import GetMoment from 'moment';

const useStyles = makeStyles(theme => ({
    root: {
        backgroundColor: theme.palette.background.paper,
        paddingTop: '0px',
        paddingBottom: '0px',
    },
    form: {
        display: 'flex',
        alignItems: 'center',
    }
}));

function Timeline() {

    const classes = useStyles();
    const auth = useContext(AuthContext);

    const [openComment, setOpenComment] = React.useState(true);
    const [commentInput, setCommentInput] = React.useState('');

    function handleDelete(id) {
        console.log(id);
        fetch('/api/social/deleteStory', {
            method: 'POST',
            body: JSON.stringify({id: id}),
            headers: {'Content-Type': 'application/json'}
        }).then(res => res.json())
        .then((user) => {
            console.log(user);
            auth.updateAuth(user);
        })
    }

    function handleCommentInput(e) {
        const { name, value } = e.target;
        setCommentInput(value);
    }

    function handleCommentSubmit(e, id) {
        e.preventDefault();
        const {username } = auth.user;
        const time = GetMoment();
        console.log(id, commentInput);
        fetch('api/social/newComment', {
            method: 'POST',
            body: JSON.stringify({id: id, comment: commentInput, username: username, time: time}),
            headers: {'Content-Type': 'application/json'}
        }).then(res => res.json())
        .then((user) => {
            console.log(user);
        });
    };

    function renderComments(comments) {
        console.log(comments)
        for (var i in comments[0]) {
            console.log(i);
            return (
                <ListItem key={i}>
                    <ListItemText primary={`${comments[0].username}: ${comments[0].comment}`} secondary={<Moment date={comments[0].time} format={'dddd h:mm a'} />} />
                </ListItem>
            );
        }
    }

    function expandComment() {
        setOpenComment(!openComment);
    }

    return (
        <div>
            <List className={classes.root}>
                {auth.user ? auth.user.Stories.map((story, index) => (
                    <Fragment key={story._id}>
                        <ListItem>
                            <ListItemAvatar><Avatar alt={story._id} src="/imgs/avatar.jpg" /></ListItemAvatar>
                            <ListItemText 
                                primary={story.story} 
                                secondary={
                                    <Moment date={story.time} format={'dddd h:mm a'} />
                                }
                            >
                            </ListItemText>
                            <IconButton onClick={expandComment}>
                                <Icon>{openComment ? 'expand_less' : 'expand_more'}</Icon>
                            </IconButton>
                            <IconButton onClick={() => handleDelete(story._id)}>
                                <Icon>delete_outline</Icon>
                            </IconButton>
                        </ListItem>
                        <Collapse in={openComment} timeout="auto" unmountOnExit>
                            <List disablePadding>
                                {renderComments(story.comments)}
                            </List>
                            <form className={classes.form} onSubmit={(e) => handleCommentSubmit(e, story._id)}>
                                <TextField
                                name='123'
                                value={commentInput}
                                variant="outlined"
                                label="Add a comment"
                                onChange={handleCommentInput}
                                >
                                </TextField>
                                    <IconButton type="submit">
                                        <Icon>add_circle</Icon>
                                    </IconButton>
                            </form>
                        </Collapse>
                        <Divider />
                    </Fragment>
                )) : ''}
            </List>
        </div>
    );
}

export default Timeline;
