import React, { Fragment, useState, useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Stepper, Step, StepLabel, Button, Typography, InputLabel, Input, FormControl, TextField } from '@material-ui/core';
import AuthContext from '../../../context/AuthContext';



const useStyles = makeStyles(theme => ({
    root: {
        width: '70%',
        position: 'absolute',
        top: '50%',
        left: '50%',
        // transform: 'translate(-50%, -50%)'
    },
    button: {
        marginRight: theme.spacing(1),
    },
    instructions: {
        marginTop: theme.spacing(1),
        marginBottom: theme.spacing(1),
    },
    inputDiv: {
        display: 'flex',
        justifyContent: 'center',
    }
}));

function getSteps() {
    return ['Confirm Password', 'Change Email'];
}

    function getStepContent(step) {
        console.log(step)
        switch (step) {
            case 0:
                return 'Authentication';
            case 1: 
                return 'Enter your new email';
            default:
                return 'Unknown step...';
        }
    }

function EditEmail() {

    const [activeStep, setActiveStep] = React.useState(0);

    const [passwordAuth, setPasswordAuth] = React.useState(false);
    const [passwordInput, setPasswordInput] = React.useState('');
    const [confirmInput, setConfirmInput] = React.useState('');  
    const [passwordErr, setPasswordErr] = React.useState(null);  

    const [emailInput, setEmailInput] = React.useState('');

    const steps = getSteps();
    const classes = useStyles();
    const auth = useContext(AuthContext);

    function handleNext() {
        setActiveStep(prevActiveStep => prevActiveStep + 1);
    }

    function handleBack() {
        setActiveStep(prevActiveStep => prevActiveStep - 1);
    }

    function handleReset() {
        setActiveStep(0);
    }

    function handlePassword(e) {
        e.preventDefault();
        const email = auth.user.email
        console.log(passwordInput, confirmInput);
        console.log(email)
        if(passwordInput === confirmInput) {
            fetch('/api/users/passwordChange', {
                method: 'POST',
                body: JSON.stringify({email: email, password: passwordInput}),
                headers: {'Content-Type': 'application/json'}
            }).then(res => res.json())
            .then((response) => {
                console.log(response);
                if(response === 'Match'){setPasswordAuth(true); setActiveStep(prevActiveStep => prevActiveStep + 1); setPasswordErr(null)}
                if(response === 'Not Authorized'){setPasswordErr(response)}
            })
        } 
        else {
            console.log('The passwords do not match!')
        }
    }

    function handleEmail(e) {
        e.preventDefault();
        const username = auth.user.username;
        console.log(username)
        fetch('/api/users/emailChange', {
            method: 'POST',
            body: JSON.stringify({username: username, email: emailInput}),
            headers: {'Content-Type': 'application/json'}
        }).then(res => res.json())
        .then((response) => {
            console.log(response);
        })
    }

    function handleInputChange(e) {
        const { name, value } = e.target
        console.log(name, value);
        if(name === 'password'){return setPasswordInput(value)}
        if(name === 'confirm'){return setConfirmInput(value)}
        if(name === 'email'){return setEmailInput(value)}
    }

    function renderForm() {
        if(!passwordAuth){
            return (
                <form onSubmit={handlePassword}>
                    <TextField required label="Enter Password" onChange={handleInputChange} value={passwordInput} name="password" variant="outlined" /> 
                    <TextField required label="Confirm Password" onChange={handleInputChange} value={confirmInput} name="confirm" variant="outlined"  />
                    <Button type="submit">
                        Submit
                    </Button>
                </form>
            );
        }
        if(passwordAuth){
            return (
                <form onSubmit={handleEmail}>
                    <TextField required label="Enter New Email" onChange={handleInputChange} value={emailInput} name="email" variant="outlined"  />
                    <Button type="submit">
                        Submit
                    </Button>
                </form>
            );
        }
    }

    return (
        <div className={classes.root}>
            <Stepper activeStep={activeStep}>
                {steps.map((label, index) => {
                    const stepProps = {};
                    const labelProps = {};
                    return (
                        <Step key={label} {...stepProps}>
                            <StepLabel {...labelProps}>{label}</StepLabel>
                        </Step>
                    );
                })}
            </Stepper>
            <div className={classes.inputDiv}>
            {activeStep === steps.length ? (
                    <div>
                        <Typography className={classes.instructions}>
                            All steps completed
                        </Typography>
                        <Button onClick={handleReset} className={classes.button}>
                            Reset
                        </Button>
                    </div>
                ): (
                    <div>
                        <Typography className={classes.instructions}>
                            {getStepContent(activeStep)}
                        </Typography>
                        {renderForm()}
                        <Typography>
                            {passwordErr ? passwordErr : ''}
                        </Typography>
                    <div>
                    <Button disabled={activeStep === 0} onClick={handleBack} className={classes.button}>
                        Back
                    </Button>
                    <Button disabled={!passwordAuth} variant="contained" color="primary" onClick={handleNext} className={classes.button}>
                        {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                    </Button>
                    </div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default EditEmail;
