import React, { useEffect, useState, useContext, Fragment } from 'react';

import {  Typography, Grid, Icon, Fab } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

import AuthContext from '../../context/AuthContext';

import DescPopover from '../../components/Showcase/Social/DescPopover';
import SocialDrawer from '../../components/Showcase/Social/SocialDrawer';
import Skyliner from '../../components/Showcase/Social/Skyliner';
import ProfileNav from '../../components/Showcase/Social/ProfileNav';
import TimelineTab from '../../components/Showcase/Social/TimelineTab';
import AboutTab from '../../components/Showcase/Social/About';
import FriendsTab from '../../components/Showcase/Social/Friends';
import PhotosTab from '../../components/Showcase/Social/Photos';

import BGImage from './imgs/1x/bacground.png';

const useStyles = makeStyles(theme => ({
    chatMenu: {
        position: 'fixed',
        bottom: '0',
        right: '0',
    },
    chatPaper: {        
        minHeight: 300,
        minWidth: 200,
        padding: '10px 10px',

    },
    chatInput: {
        position: 'absolute',
        bottom: 0,
        display: 'flex',
        margin: '0px 10px',
    },
    chatList: {
        display: 'flex',
        justifyContent: 'flex-start',
        paddingInlineStart: '0px'
    },   
    listItem: {
        padding: '0px 0px 0px 0px'
    },
    socialHeader: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        marginTop: 50,
    },
    conceptContainer: {},
    socialMedia: {
        [theme.breakpoints.down('md')]: {
            width: '70%',
            margin: '10px auto',
        },
        [theme.breakpoints.down('sm')]: {
            width: '80%',
            margin: '10px auto',
        },
        [theme.breakpoints.down('xs')]: {
            width: '80%',
            margin: '10px auto 10px auto',
        },
        width: 600,
        margin: '10px auto 30px auto',
        padding: 40
    },
    background: {
        backgroundImage: `url(${BGImage})`,
        backgroundSize: 'cover',
        height: '50vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        position: 'relative',
        zIndex: 1302
    },
    intro: {
        [theme.breakpoints.down('xs')]: {
            height: '100vh',
        },
        height: '100vh',
        width: '100%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'flex-end',
    },
    whiteOut: {
        backgroundColor: 'white',
        position: 'relative',
    },
}));

const Profile = () => {

    const [user, setUser] = useState();
    const [profileTimeline, setProfileTimeline] = useState(true);
    const [About, setAbout] = useState(false);
    const [Friends, setFriends] = useState(false);
    const [Photos, setPhotos] = useState(false);
    const [open, setOpen] = useState(true);

    const classes = useStyles();

    const auth = useContext(AuthContext);

    const handleClick = () => {
        setOpen(!open);
    };

    const handleTabChange = (tab) => {
        switch (tab) {
            case 'Timeline':
                setProfileTimeline(true);
                setAbout(false);
                setFriends(false);
                setPhotos(false);
                break;
            case 'About':
                setAbout(true);
                setProfileTimeline(false);
                setFriends(false);
                setPhotos(false);
                break;
            case 'Friends':
                setFriends(true);
                setProfileTimeline(false);
                setAbout(false);
                setPhotos(false);
                break;
            case 'Photos': 
                setPhotos(true);
                setProfileTimeline(false);
                setAbout(false);
                setFriends(false);
                break;
            default:
                break; 
        }
    };

    const renderTabs = () => {
        if (!user) { return }
        switch (true) {
            case profileTimeline:
                return <TimelineTab page={{handle: 'HeathBanner'}} />;
            case About:
                return <AboutTab handle={{handle: 'HeathBanner'}} auth={auth.user} info={user.info} />;
            case Friends:
                return <FriendsTab user={user} />;
            case Photos:
                return <PhotosTab info={user.info} />;
            default:
                return <TimelineTab page={{handle: 'HeathBanner'}} />;
        }
    };

    useEffect(() => {
        const username = 'HeathBanner'
        fetch('/api/social/getProfile', {
            method: 'POST',
            body: JSON.stringify({ username }),
            headers: { 'Content-Type': 'application/json' }
        })
            .then(res => res.json())
            .then((result) => {
                setUser(result);
            });
    }, []);

    return (
        <Fragment>

            <Grid container>

                <Grid className={classes.socialHeader} item xs={12}>

                    <Typography className={classes.wip} variant="h1" align="center" color="primary">
                        Social Media Concept
                    </Typography>
                    <DescPopover />

                </Grid>

                <Grid className={classes.background} item xs={12}>


                </Grid>

                <Grid style={{ zIndex: 1301 }} className={classes.whiteOut} item xs={12}>

                    <SocialDrawer />

                </Grid>
                <Grid style={{ zIndex: 1300 }} className={classes.whiteOut} item xs={12}>

                    <Skyliner user={user} handleClick={handleClick} auth={auth.user} username={'HeathBanner'} />

                </Grid>
                <Grid style={{ zIndex: 1300 }} item xs={12}>

                    <ProfileNav  tabChange={handleTabChange} />

                </Grid>
                <Grid style={{ backgroundColor: 'white' }} item xs={12}>

                    {renderTabs()}

                </Grid>

            </Grid>

        </Fragment>
    );
};

export default Profile;
