import React, { Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Avatar, Typography } from '@material-ui/core';

const Photo = 'https://media.licdn.com/dms/image/C5603AQHqTyUqMrqJZA/profile-displayphoto-shrink_200_200/0?e=1569456000&v=beta&t=YtZoiTHVJEXn3syxgpM5VVg7zTMFjD6SXZLVKHh9JB8';

const useStyles = makeStyles(theme => ({
    container: {
        position: 'relative',
        height: 200,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    avatar: {
        height: 200,
        width: 200,
        position: 'absolute',
        top: 20,
        left: 20,
    },
    skillsHeader: {
        color: 'white',
    },
    spacer: {
        height: '80vh',
    },
}));

const Introduction = () => {

    const classes = useStyles();

    return (
        <Fragment>

            <Grid className={classes.container} item xs={12}>

                <Avatar className={classes.avatar} src={Photo} alt="Heath Banner"  />   

                <Typography className={classes.skillsHeader} variant="h1" align="center">
                    A Little About Me
                </Typography>         

            </Grid>
            <Grid className={classes.spacer}>



            </Grid>

        </Fragment>
    );
};

export default Introduction;
