import React, { useContext, Fragment, useState } from 'react';
import AuthContext from '../../../context/AuthContext';
import Moment from 'react-moment';

import { makeStyles } from '@material-ui/core/styles';
import { Collapse, List, ListItem, ListItemText, ListItemAvatar, Divider, Typography, Avatar, Icon, Fab} from '@material-ui/core';

const useStyles = makeStyles(theme => ({
    root: {
        backgroundColor: theme.palette.background.paper,
        paddingTop: '0px',
        paddingBottom: '0px',
    },
}));

function Timeline() {

    const classes = useStyles();
    const auth = useContext(AuthContext);

    const [openComment, setOpenComment] = React.useState(false);

    function handleDelete(id) {
        console.log(id);
        fetch('/api/social/deleteStory', {
            method: 'POST',
            body: JSON.stringify({id: id}),
            headers: {'Content-Type': 'application/json'}
        }).then(res => res.json())
        .then((user) => {
            console.log(user);
            auth.updateAuth(user);
        })
    }

    function expandComment() {
        setOpenComment(!openComment);
    }

    return (
        <div>
            <List className={classes.root}>
                {auth.user ? auth.user.Stories.map((story, index) => (
                    <Fragment key={story._id}>
                        <ListItem>
                            <ListItemAvatar><Avatar alt={story._id} src="/imgs/avatar.jpg" /></ListItemAvatar>
                            <ListItemText 
                                primary={story.story} 
                                secondary={
                                    <Moment date={story.time} format={'dddd h:mm a'} />
                                }
                            >
                            </ListItemText>
                            <IconButton onClick={expandComment}>
                                <Icon>{openComment ? 'expand_less' : 'expand_more'}</Icon>
                            </IconButton>
                            <IconButton onClick={() => handleDelete(story._id)}>
                                <Icon>delete_outline</Icon>
                            </IconButton>
                        </ListItem>
                        <Collapse in={openComment} timeout="auto" unmountOnExit>
                            <List disablePadding>
                                <ListItem>
                                    <ListItemText primary="THIS IS AWESOME" />
                                </ListItem>
                            </List>
                        </Collapse>
                        <Divider />
                    </Fragment>
                )) : ''}
            </List>
        </div>
    );
}

export default Timeline;
