import React, { Fragment } from 'react';
import { Link } from 'react-router-dom';

import { makeStyles } from '@material-ui/core/styles';
import { Icon, IconButton, TextField, Card, CardActionArea, CardActions, CardContent, CardMedia, Button, Typography} from '@material-ui/core';

const useStyles = makeStyles({
    form: {
        margin: '20px auto',
        display: 'flex',
        alignItems: 'center',
        width: '80%',
    },
    card: {
        maxWidth: 345,
        margin: '50px auto',
    },
    media: {
        height: 140,
    },
});


function NewFriends() {

    const classes = useStyles();

    const [searchInput, setSearchInput] = React.useState('');
    const [searchResults, setSearchResults] = React.useState('');

    function handleInputChange(e) {
        const { value } = e.target;
        setSearchInput(value);
    };

    function handleSubmit(e) {
        e.preventDefault();
        console.log(searchInput);
        fetch('api/social/newFriends', {
            method: 'POST',
            body: JSON.stringify({username: searchInput}),
            headers: {'Content-Type': 'application/json'}
        }).then(res => res.json())
        .then((user) => {
            console.log(user);
            setSearchResults(user);
        });
    }

    function renderFriends() {

        return (
            searchResults.map((user, index) => {
                return (
                    <Card key={index} className={classes.card}>
                        <CardActionArea>
                            <CardMedia
                                className={classes.media}
                                image="#"
                                title={user.username}
                            />
                            <CardContent>
                                <Typography gutterBottom variant="h4" component="h2">
                                    {user.username}
                                </Typography>
                                <Typography variant="h6" color="textSecondary" component="p">
                                    Front-End Developer
                                </Typography>
                            </CardContent>
                        </CardActionArea>
                        <CardActions>
                            <Button>
                                View Profile
                            </Button>

                            <Link to={`/social/${user.username}`}><Button>
                                Add Friend
                            </Button></Link>

                        </CardActions>
                    </Card>
                );
            })
        )
    }

    return (
        <Fragment>
            <form onSubmit={handleSubmit} className={classes.form}>
                <TextField variant="outlined" name="friendSearch" value={searchInput} label="Find Friends!" onChange={handleInputChange} />
                <IconButton type="submit" size="medium">
                    <Icon fontSize="large">search</Icon>
                </IconButton>
            </form>
                {searchResults ? renderFriends() : ''}
        </Fragment>
    );
}

export default NewFriends;
