import React, { useEffect, useState } from 'react';

import SocialDrawer from '../../components/Navigation/SocialDrawer';
import Skyliner from '../../components/Social/Profile/Skyliner';

function Profile(props) {

    const [user, setUser] = React.useState('');
    const [onLoad, setOnLoad] = React.useState(false);

    useEffect(() => {
        const { handle } = props.match.params
        setUser(handle);
        setOnLoad(true);
        if(!onLoad){
            fetch('/api/social/getProfile', {
                method: 'POST',
                body: JSON.stringify({username: handle}),
                headers: {'Content-Type': 'application/json'}
            }).then(res => res.json())
            .then((user) => {
                console.log(user);
            });
        }
    })

    return (
        <div className="container-fluid colp">
            <div className="row">
                <div className="col-12">
                    <SocialDrawer />
                </div>
            </div>
            <div className="row">
                <div className="col-12">
                    <Skyliner user={user} skyliner="http://getwallpapers.com/wallpaper/full/6/8/c/326021.jpg" />
                </div>
            </div>
        </div>
    );
}

export default Profile;
