import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {
    Grid,
    Typography,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    container: {
        backgroundColor: '#0000a2',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexWrap: 'wrap',
        flexGrow: 1,
    },
    why: {
        color: 'white',
    },
    whyContainers: {
        flexShrink: 3,
    },
}));

const Why = () => {

    const classes = useStyles();

    return (
        <Grid className={classes.container} item xs={12}>

            <Typography
                className={classes.why}
                variant="h4"
                align="center"
            >
                Why Heath?
            </Typography>

            <div className={classes.whyContainers}>
                <Typography
                    variant="h5"
                >
                    Experience
                </Typography>
                <Typography>
                    Software development best practices applied on over 500 
                    products in London, Paris and New York
                </Typography>
            </div>
            <div className={classes.whyContainers}>
                <Typography
                    variant="h5"
                >
                    Time To Market
                </Typography>
                <Typography>
                    Launch new software in weeks, not months
                </Typography>
            </div>
            <div className={classes.whyContainers}>
                <Typography
                    variant="h5"
                >
                    Continuous Improvement
                </Typography>
                <Typography>
                    Embracing Lean and Kaizen, the Japanese practices for 
                    continuous improvement, allows us to release quickly without 
                    compromising quality
                </Typography>
            </div>

        </Grid>
    );
};

export default Why;
