import React, { useEffect } from 'react';

import { emphasize, makeStyles } from '@material-ui/core/styles';
import { Typography, NoSsr, TextField, Paper, MenuItem, Button, Icon } from '@material-ui/core';

import PropTypes from 'prop-types';
import Select from 'react-select';


const useStyles = makeStyles(theme => ({
  root: {
    [theme.breakpoints.up('lg')]: {
      height: 250,
      width: '50%',
      margin: '50px auto',  
    },
    [theme.breakpoints.down('lg')]: {
      height: 250,
      width: '70%',
      margin: '50px auto',  
    },
    [theme.breakpoints.down('sm')]: {
      height: 250,
      width: '90%',
      margin: '20px auto 10px auto',  
    },
    [theme.breakpoints.down('xs')]: {
        height: 250,
        width: '90%',
        margin: '20px auto 10px auto',
    },
    flexGrow: 1,
    background: 'rgb(0, 0, 0, 0.4)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: '5px',

  },
  form: {
    width: '70%'
  },
  input: {
    display: 'flex',
    padding: 20,
    height: 'auto',
    width: '100%',
    color: 'white !important',
    fontSize: '3.2rem',
    '&:hover': {
      cursor: 'pointer',
    }
  },
  valueContainer: {
    display: 'flex',
    flexWrap: 'wrap',
    flex: 1,
    alignItems: 'center',
    overflow: 'hidden',
    borderBottom: '1px solid white',
    color: 'white !important',
    '&:hover': {
      cursor: 'text',
    }
  },
  chip: {
    margin: theme.spacing(0.5, 0.25),
    color: 'white',
  },
  chipFocused: {
    backgroundColor: emphasize(
      theme.palette.type === 'light' ? theme.palette.grey[300] : theme.palette.grey[900],
      0.08,
    ),
  },
  noOptionsMessage: {
    padding: theme.spacing(1, 2),
  },
  singleValue: {
    fontSize: 16,
    color: 'white !important'
  },
  placeholder: {
    position: 'absolute',
    left: 2,
    bottom: 6,
    fontSize: 16,
    color: 'white'
  },
  paper: {
    position: 'absolute',
    zIndex: 1,
    marginTop: theme.spacing(1),
    left: 0,
    right: 0,
  },
  divider: {
    height: theme.spacing(2),
  },
  button: {
    background: 'rgba(0, 0, 0, 0.08)',
    color: 'white',
    margin: '20px auto',
    '&:hover': {
      background: 'rgb(68, 192, 255, 0.7)',
    },
  },
  hover: {
    background: 'rgb(232, 240, 255, 0.8)',
  },
  underline: {
    '&:before': {
        borderBottom: '1px solid #fcdb0d !important',
    },
    '&:after': {
        borderBottom: '2px solid #00d0ff !important',
    },
    '&:hover:before': {
        borderBottom: '2px solid #fcdb0d !important',
    },
  },
  label: {
    '&.Mui-focused': {
      color: '#ffbb19'
    },
    color: 'rgb(255, 255, 255, 0.6)'
  },
  icon: {
    '&:hover': {
      color: '#00d0ff'  
    },
    color: '#fcdb0d'
  }
}));

function NoOptionsMessage(props) {
  return (
    <Typography
      color="textSecondary"
      className={props.selectProps.classes.noOptionsMessage}
      {...props.innerProps}
    >
      {props.children}
    </Typography>
  );
}

NoOptionsMessage.propTypes = {
  children: PropTypes.node,
  innerProps: PropTypes.object,
  selectProps: PropTypes.object.isRequired,
};

function inputComponent({ inputRef, ...props }) {
  return <div ref={inputRef} {...props} />;
}

inputComponent.propTypes = {
  inputRef: PropTypes.oneOfType([PropTypes.func, PropTypes.object]),

};

function Control(props) {
  const {
    children,
    innerProps,
    innerRef,
    selectProps: { classes, TextFieldProps },
  } = props;

  return (
    <TextField
      fullWidth
      style={{
        color: 'white',
      }}
      onChange= {props.selectProps.onChange}
      InputProps={{
        inputComponent,
        className: classes.underline,
        inputProps: {
          onChange: props.selectProps.onChange,
          className: classes.input,
          ref: innerRef,
          children,
          ...innerProps,
        },
      }}
      {...TextFieldProps}
    />
  );
}

Control.propTypes = {
  children: PropTypes.node,
  innerProps: PropTypes.object,
  innerRef: PropTypes.oneOfType([PropTypes.func, PropTypes.object]),
  selectProps: PropTypes.object.isRequired,
};

function Option(props) {
  return (
    <MenuItem
      ref={props.innerRef}
      selected={props.isFocused}
      component="div"
      style={{
        fontWeight: props.isSelected ? 500 : 400,
      }}
      {...props.innerProps}
    >
      {props.children}
    </MenuItem>
  );
}

Option.propTypes = {
  children: PropTypes.node,
  innerProps: PropTypes.object,
  innerRef: PropTypes.oneOfType([PropTypes.func, PropTypes.object]),
  isFocused: PropTypes.bool,
  isSelected: PropTypes.bool,
};

function Placeholder(props) {
  return (
    <Typography
      color="textSecondary"
      className={props.selectProps.classes.placeholder}
      {...props.innerProps}
    >
      {props.children}
    </Typography>
  );
}

Placeholder.propTypes = {
  children: PropTypes.node,
  innerProps: PropTypes.object,
  selectProps: PropTypes.object.isRequired,
};

function SingleValue(props) {
  return (
    <Typography className={props.selectProps.classes.singleValue} {...props.innerProps}>
      {props.children}
    </Typography>
  );
}

SingleValue.propTypes = {
  children: PropTypes.node,
  innerProps: PropTypes.object,
  selectProps: PropTypes.object.isRequired,
};

function DropdownIndicator(props) {

  const {
    selectProps: { classes },
  } = props;

  return (
    <Icon className={classes.icon}>keyboard_arrow_down</Icon>
  );
}

SingleValue.propTypes = {
  children: PropTypes.node,
  innerProps: PropTypes.object,
  selectProps: PropTypes.object.isRequired,
};


function ValueContainer(props) {


  return <div className={props.selectProps.classes.valueContainer} >{props.children}</div>;
}

ValueContainer.propTypes = {
  children: PropTypes.node,
  selectProps: PropTypes.object.isRequired,
};


function Menu(props) {
  return (
    <Paper square className={props.selectProps.classes.paper} {...props.innerProps}>
      {props.children}
    </Paper>
  );
}

Menu.propTypes = {
  children: PropTypes.node,
  innerProps: PropTypes.object,
  selectProps: PropTypes.object,
};

const components = {
  Control,
  Menu,
  NoOptionsMessage,
  Option,
  Placeholder,
  SingleValue,
  ValueContainer,
  DropdownIndicator,
};

const states = [
  {label: 'Alabama'},
  {label: 'Alaska'},
  {label: 'Arizona'},
  {label: 'Arkansas'},
  {label: 'California'},
  {label: 'Colorado'},
  {label: 'Connecticut'},
  {label: 'Delaware'},
  {label: 'Florida'},
  {label: 'Georgia'},
  {label: 'Hawaii'},
  {label: 'Idaho'},
  {label: 'Illinois'},
  {label: 'Indiana'},
  {label: 'Iowa'},
  {label: 'Kansas'},
  {label: 'Louisiana'},
  {label: 'Maine'},
  {label: 'Maryland'},
  {label: 'Massachusetts'},
  {label: 'Michigan'},
